import axios from "axios";

const API_BASE = "http://localhost:8080/api";

export const getComponents = (type) => axios.get(`${API_BASE}/${type}`);
export const calculate = (data) => axios.post(`${API_BASE}/calculate`, data);
export const calculateSolarPotential = (data) => axios.post(`${API_BASE}/solar-potential`, data);

// Location and map-related APIs
export const searchLocations = (data) => axios.post(`${API_BASE}/location/search`, data);
export const reverseGeocode = (lat, lng) => axios.get(`${API_BASE}/location/reverse-geocode?lat=${lat}&lng=${lng}`);
export const calculatePolygonArea = (data) => axios.post(`${API_BASE}/location/calculate-area`, data);
