import React, { useState } from 'react';
import { calculateSolarPotential } from './solarPotentialUtils';

const RoofConfigurationForm = ({ roofConfig, onConfigChange, projectedArea, actualRoofArea, visible = true, addedRoofs = [], onRoofsUpdate }) => {
  const [showAngleTooltip, setShowAngleTooltip] = useState(false);
  const [showShadingTooltip, setShowShadingTooltip] = useState(false);
  const [showUtilisationTooltip, setShowUtilisationTooltip] = useState(false);
  const [solarPotentialResult, setSolarPotentialResult] = useState(null);
  const [showPotentialResults, setShowPotentialResults] = useState(false);

  if (!visible) return null;

  // Generate roof angle options from 0 to 90 in increments of 10
  const roofAngleOptions = Array.from({ length: 10 }, (_, i) => i * 10);

  // Determine which area to display
  const displayArea = actualRoofArea || projectedArea;

  // Get next roof number (highest existing ID + 1)
  const getNextRoofNumber = () => {
    if (addedRoofs.length === 0) return 1;
    return Math.max(...addedRoofs.map(roof => roof.id)) + 1;
  };

  const currentRoofNumber = getNextRoofNumber();

  // Check if all required fields are filled for solar potential calculation
  const isReadyForCalculation = () => {
    return displayArea > 0 &&
           roofConfig.roofOrientation &&
           roofConfig.roofAngle !== '' &&
           roofConfig.roofShading &&
           roofConfig.roofUtilisation;
  };

  const handleSolarPotentialCalculation = () => {
    if (!isReadyForCalculation()) return;

    const result = calculateSolarPotential(
      displayArea,
      roofConfig.roofShading,
      roofConfig.roofUtilisation,
      parseInt(roofConfig.roofAngle),
      roofConfig.roofOrientation
    );

    setSolarPotentialResult(result);
    setShowPotentialResults(true);
  };

  const handleAddRoof = () => {
    if (!solarPotentialResult) return;

    const newRoof = {
      id: currentRoofNumber,
      area: displayArea,
      orientation: roofConfig.roofOrientation,
      angle: roofConfig.roofAngle,
      shading: roofConfig.roofShading,
      utilisation: roofConfig.roofUtilisation,
      solarPotential: solarPotentialResult.solarPotential,
      config: { ...roofConfig }
    };

    const updatedRoofs = [...addedRoofs, newRoof];
    onRoofsUpdate(updatedRoofs);

    // Reset for next roof
    setShowPotentialResults(false);
    setSolarPotentialResult(null);

    // Clear current configuration for next roof
    onConfigChange('roofOrientation', '');
    onConfigChange('roofAngle', '');
    onConfigChange('roofShading', '');
    onConfigChange('roofUtilisation', '');
  };

  const handleRemoveRoof = (roofId) => {
    const updatedRoofs = addedRoofs.filter(roof => roof.id !== roofId);
    onRoofsUpdate(updatedRoofs);
  };

  const getOrientationLabel = (orientation) => {
    const labels = {
      'n': 'North', 'ne': 'Northeast', 'ene': 'East-Northeast',
      'e': 'East', 'ese': 'East-Southeast', 'se': 'Southeast',
      's': 'South', 'sw': 'Southwest', 'wsw': 'West-Southwest',
      'w': 'West', 'wnw': 'West-Northwest', 'nw': 'Northwest'
    };
    return labels[orientation] || orientation;
  };

  const getShadingLabel = (shading) => {
    const labels = {
      'no-shading': 'No Shading', 'light': 'Light', 'moderate': 'Moderate',
      'heavy': 'Heavy', 'extreme': 'Extreme'
    };
    return labels[shading] || shading;
  };

  const getUtilisationLabel = (utilisation) => {
    const labels = {
      'minimal-obstacles': 'Minimal Obstacles', 'slightly-complex': 'Slightly Complex',
      'moderately-complex': 'Moderately Complex', 'highly-complex': 'Highly Complex',
      'extremely-complex': 'Extremely Complex'
    };
    return labels[utilisation] || utilisation;
  };

  const toggleAngleTooltip = () => {
    setShowAngleTooltip(!showAngleTooltip);
  };

  const toggleShadingTooltip = () => {
    setShowShadingTooltip(!showShadingTooltip);
  };

  const toggleUtilisationTooltip = () => {
    setShowUtilisationTooltip(!showUtilisationTooltip);
  };

  return (
    <div className="roof-configuration">
      <h4>Roof {currentRoofNumber} Details</h4>

      {/* Roof Size Display - Simplified */}
      {projectedArea > 0 && (
        <div className="roof-size-display">
          <span className="roof-size-label">Roof Size:</span>
          <strong className="roof-size-value">{displayArea.toFixed(2)} m</strong>
        </div>
      )}

      <div className="config-grid">
        <div className="form-group">
          <label htmlFor="roofOrientation">Roof Orientation</label>
          <select
            id="roofOrientation"
            value={roofConfig.roofOrientation}
            onChange={(e) => onConfigChange('roofOrientation', e.target.value)}
            className="form-select"
            required
          >
            <option value="">Select orientation</option>
            <option value="n">North (N)</option>
            <option value="ne">Northeast (NE)</option>
            <option value="ene">East-Northeast (ENE)</option>
            <option value="e">East (E)</option>
            <option value="ese">East-Southeast (ESE)</option>
            <option value="se">Southeast (SE)</option>
            <option value="s">South (S)</option>
            <option value="sw">Southwest (SW)</option>
            <option value="wsw">West-Southwest (WSW)</option>
            <option value="w">West (W)</option>
            <option value="wnw">West-Northwest (WNW)</option>
            <option value="nw">Northwest (NW)</option>
          </select>
        </div>

        <div className="form-group">
          <div className="form-label-with-help">
            <label htmlFor="roofAngle">Roof Angle</label>
            <button 
              type="button"
              className="field-help-btn"
              onClick={toggleAngleTooltip}
              aria-label="Learn about roof angle impact"
            >
              ?
            </button>
          </div>
          <select
            id="roofAngle"
            value={roofConfig.roofAngle}
            onChange={(e) => onConfigChange('roofAngle', e.target.value)}
            className="form-select"
            required
          >
            <option value="">Select angle</option>
            {roofAngleOptions.map(angle => (
              <option key={angle} value={angle}>{angle} {angle === 0 ? '(Flat)' : ''}</option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <div className="form-label-with-help">
            <label htmlFor="roofShading">Roof Shading</label>
            <button 
              type="button"
              className="field-help-btn"
              onClick={toggleShadingTooltip}
              aria-label="Learn about roof shading levels"
            >
              ?
            </button>
          </div>
          <select
            id="roofShading"
            value={roofConfig.roofShading}
            onChange={(e) => onConfigChange('roofShading', e.target.value)}
            className="form-select"
            required
          >
            <option value="">Select shading level</option>
            <option value="no-shading">No Shading</option>
            <option value="light">Light Shading</option>
            <option value="moderate">Moderate Shading</option>
            <option value="heavy">Heavy Shading</option>
            <option value="extreme">Extreme Shading</option>
          </select>
        </div>

        <div className="form-group">
          <div className="form-label-with-help">
            <label htmlFor="roofUtilisation">Roof Utilisation</label>
            <button 
              type="button"
              className="field-help-btn"
              onClick={toggleUtilisationTooltip}
              aria-label="Learn about roof utilisation levels"
            >
              ?
            </button>
          </div>
          <select
            id="roofUtilisation"
            value={roofConfig.roofUtilisation}
            onChange={(e) => onConfigChange('roofUtilisation', e.target.value)}
            className="form-select"
            required
          >
            <option value="">Select complexity level</option>
            <option value="minimal-obstacles">Minimal Obstacles</option>
            <option value="slightly-complex">Slightly Complex</option>
            <option value="moderately-complex">Moderately Complex</option>
            <option value="highly-complex">Highly Complex</option>
            <option value="extremely-complex">Extremely Complex</option>
          </select>
        </div>
      </div>

      {/* Solar Potential Calculation Section */}
      {isReadyForCalculation() && (
        <div className="solar-potential-section">
          <button 
            type="button"
            className="btn btn-calculate"
            onClick={handleSolarPotentialCalculation}
          >
            Calculate Solar Potential
          </button>
        </div>
      )}

      {/* Solar Potential Results */}
      {showPotentialResults && solarPotentialResult && (
        <div className="solar-potential-results">
          <div className="potential-result-header">
            <h4>Roof {currentRoofNumber}'s solar potential: {solarPotentialResult.solarPotential.toFixed(2)}</h4>
            <button 
              type="button"
              className="btn btn-add-roof"
              onClick={handleAddRoof}
            >
              Add this roof
            </button>
          </div>
        </div>
      )}

      {/* Added Roofs Display */}
      {addedRoofs.length > 0 && (
        <div className="added-roofs-section">
          <h4>Added Roofs</h4>
          <div className="roofs-grid">
            {addedRoofs.map((roof) => (
              <div key={roof.id} className="roof-card">
                <div className="roof-card-header">
                  <h5>Roof {roof.id}</h5>
                  <button 
                    className="btn-remove-roof"
                    onClick={() => handleRemoveRoof(roof.id)}
                    aria-label="Remove roof"
                  >

                  </button>
                </div>
                <div className="roof-card-content">
                  <div className="roof-detail">
                    <span className="detail-label">Area:</span>
                    <span className="detail-value">{roof.area.toFixed(2)} m</span>
                  </div>
                  <div className="roof-detail">
                    <span className="detail-label">Orientation:</span>
                    <span className="detail-value">{getOrientationLabel(roof.orientation)}</span>
                  </div>
                  <div className="roof-detail">
                    <span className="detail-label">Angle:</span>
                    <span className="detail-value">{roof.angle}</span>
                  </div>
                  <div className="roof-detail">
                    <span className="detail-label">Shading:</span>
                    <span className="detail-value">{getShadingLabel(roof.shading)}</span>
                  </div>
                  <div className="roof-detail">
                    <span className="detail-label">Utilisation:</span>
                    <span className="detail-value">{getUtilisationLabel(roof.utilisation)}</span>
                  </div>
                  <div className="roof-detail solar-potential">
                    <span className="detail-label">Solar Potential:</span>
                    <span className="detail-value">{roof.solarPotential.toFixed(2)}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Total Solar Potential */}
          <div className="total-potential">
            <h4>Total Solar Potential: {addedRoofs.reduce((sum, roof) => sum + roof.solarPotential, 0).toFixed(2)}</h4>
          </div>
        </div>
      )}

      {/* Roof Angle Help Modal - Using Same Style as BasicInformation */}
      {showAngleTooltip && (
        <div className="field-help-overlay" onClick={toggleAngleTooltip}>
          <div className="field-help-modal" onClick={(e) => e.stopPropagation()}>
            <div className="field-help-header">
              <h3>Roof Angle Impact</h3>
              <button className="field-help-close-btn" onClick={toggleAngleTooltip} aria-label="Close help"></button>
            </div>
            <div className="field-help-content">
              <div className="help-image-container">
                <img 
                  src="/images/help/roof_angle_guide.png" 
                  alt="Roof Angle Guide Illustration" 
                  className="help-guide-image"
                />
              </div>
              <p><strong>How roof angle affects your roof area calculation:</strong></p>
              <p>The roof angle determines the actual surface area available for solar panels. Google Maps measures the horizontal projected area (as seen from above), but the actual roof surface is larger when the roof is angled.</p>
              <p><strong>Calculation Formula:</strong></p>
              <p>Actual Roof Area = Projected Area  cos(roof angle)</p>
              <p><strong>Examples:</strong></p>
              <p> <strong>Flat roof (0):</strong> Actual area = projected area (no change)</p>
              <p> <strong>30 roof:</strong> Actual area is 15% larger (1.15)</p>
              <p> <strong>45 roof:</strong> Actual area is 41% larger (1.41)</p>
              <p><strong>Why this matters:</strong></p>
              <p>A steeper roof provides more actual surface area for solar panels than what appears when viewed from above. This directly affects how many solar panels can be installed and your potential energy generation.</p>
            </div>
          </div>
        </div>
      )}

      {/* Roof Shading Help Modal */}
      {showShadingTooltip && (
        <div className="field-help-overlay" onClick={toggleShadingTooltip}>
          <div className="field-help-modal" onClick={(e) => e.stopPropagation()}>
            <div className="field-help-header">
              <h3>Roof Shading Guide</h3>
              <button 
                className="field-help-close-btn"
                onClick={toggleShadingTooltip}
                aria-label="Close help"
              >

              </button>
            </div>
            <div className="field-help-content">
              <p>Roof shading affects solar panel performance throughout the day. Choose the option that best describes your roof's shading condition:</p>

              <p><strong>No Shading (1.0)</strong></p>
              <p>Roof surroundings are unobstructed, with full sunlight all day.</p>

              <p><strong>Light Shading (0.90)</strong></p>
              <p>Small portion blocked in early morning/evening, but no obstruction during midday.</p>

              <p><strong>Moderate Shading (0.70)</strong></p>
              <p>One side has close-range obstruction, reducing sunlight hours by half.</p>

              <p><strong>Heavy Shading (0.50)</strong></p>
              <p>Two or more sides obstructed, roof is in shadow most of the time.</p>

              <p><strong>Extreme Shading (0.20)</strong></p>
              <p>Almost no sunlight from dawn to dusk, sun cannot reach the roof. Solar installation not recommended.</p>
            </div>
          </div>
        </div>
      )}

      {/* Roof Utilisation Help Modal */}
      {showUtilisationTooltip && (
        <div className="field-help-overlay" onClick={toggleUtilisationTooltip}>
          <div className="field-help-modal" onClick={(e) => e.stopPropagation()}>
            <div className="field-help-header">
              <h3>Roof Utilisation Guide</h3>
              <button 
                className="field-help-close-btn"
                onClick={toggleUtilisationTooltip}
                aria-label="Close help"
              >

              </button>
            </div>
            <div className="field-help-content">
              <p>Roof utilisation refers to how suitable your roof is for solar panel installation based on its shape, obstacles, and complexity:</p>

              <p><strong>Minimal Obstacles</strong></p>
              <p>Regular shape, no obstacles, unobstructed. Typical rectangular roof, clean new construction.</p>

              <p><strong>Slightly Complex</strong></p>
              <p>Few skylights/chimneys, slightly irregular edges. Common residential roof, light trapezoidal shape.</p>

              <p><strong>Moderately Complex</strong></p>
              <p>Irregular boundaries, multiple obstacles, significant trapezoidal shape. Multiple chimneys/courtyards or complex edges.</p>

              <p><strong>Highly Complex</strong></p>
              <p>Many protrusions, severely irregular boundaries, difficult module placement. Old or multi-slope roofs.</p>

              <p><strong>Extremely Complex</strong></p>
              <p>Narrow, fragmented roof, extremely poor layout. Almost impossible to arrange panels at scale.</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RoofConfigurationForm;
