import React, { useState } from 'react';
import { postcodeService } from '../../../services/postcodeService';

const PostcodeInput = ({ onLocationSelect, isLoading, setIsLoading }) => {
  const [postcode, setPostcode] = useState('');
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const value = e.target.value.toUpperCase();
    setPostcode(value);
    setError('');
  };

  const handleSearch = async () => {
    if (!postcode.trim()) {
      setError('Please enter a postcode');
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      const result = await postcodeService.validatePostcode(postcode);

      if (result.success) {
        onLocationSelect({
          type: 'postcode',
          postcode: result.data.postcode,
          latitude: result.data.latitude,
          longitude: result.data.longitude,
          location: `${result.data.admin_district}, ${result.data.region}`,
          country: result.data.country
        });
      } else {
        setError(result.error || 'Invalid postcode');
      }
    } catch (error) {
      setError('Failed to validate postcode');
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  // Ê∑ªÂä†ÊºîÁ§∫Êï∞ÊçÆÂäüËÉΩÔºåÁî®‰∫éÂêéÁ´Ø‰∏çÂèØÁî®Êó∂ÁöÑÊµãËØï
  const handleDemoData = () => {
    setIsLoading(true);
    setError('');

    // Ê®°ÊãüÂä†ËΩΩÂª∂Ëøü
    setTimeout(() => {
      onLocationSelect({
        type: 'postcode',
        postcode: 'SW1A 1AA',
        latitude: 51.5014,
        longitude: -0.1419,
        location: 'Westminster, London',
        country: 'England',
        city: 'Westminster',
        region: 'London'
      });
      setIsLoading(false);
    }, 1000);
  };

  return (
    <div className="postcode-input-container">
      <h3>Enter Your Postcode</h3>
      <p>Enter your UK postcode to find your exact location</p>

      <div className="input-group">
        <input
          type="text"
          value={postcode}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          placeholder="e.g., SW1A 1AA"
          className={`postcode-input ${error ? 'error' : ''}`}
          disabled={isLoading}
        />
        <button
          onClick={handleSearch}
          disabled={isLoading || !postcode.trim()}
          className="btn btn-primary search-btn"
        >
          {isLoading ? 'Searching...' : 'Search'}
        </button>
      </div>

      {error && (
        <div className="error-message">
          <span>Warning: {error}</span>
        </div>
      )}

      {/* ÊºîÁ§∫Êï∞ÊçÆÊåâÈíÆ */}
      <div className="demo-section" style={{ marginTop: '1rem', padding: '1rem', backgroundColor: '#f8f9fa', borderRadius: '8px', border: '1px dashed #dee2e6' }}>
        <div style={{ marginBottom: '0.5rem' }}>
          <strong>üöÄ For Demo/Testing (when backend is not available):</strong>
        </div>
        <button
          onClick={handleDemoData}
          disabled={isLoading}
          className="btn btn-secondary"
          style={{ marginRight: '0.5rem' }}
        >
          {isLoading ? 'Loading...' : 'Use Demo Data (Westminster, London)'}
        </button>
        <div style={{ fontSize: '0.85rem', color: '#6c757d', marginTop: '0.5rem' }}>
          This will load sample location data and climate charts for demonstration purposes.
        </div>
      </div>

      <div className="postcode-examples">
        <small>
          <strong>Examples:</strong> KY16 8DE (St Andrews), SW1A 1AA (Westminster), M1 1AA (Manchester)
        </small>
      </div>
    </div>
  );
};

export default PostcodeInput;
