import React, { useState, useEffect, useRef } from 'react';
import { Wrapper, Status } from '@googlemaps/react-wrapper';

const GoogleMapsPolygonDrawer = ({ onAreaChange, userLocation, apiKey }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [polygon, setPolygon] = useState(null);
  const [area, setArea] = useState(0);

  useEffect(() => {
    if (mapRef.current && !map) {
      const newMap = new window.google.maps.Map(mapRef.current, {
        center: userLocation || { lat: 51.5074, lng: -0.1278 }, // London default
        zoom: 20,
        mapTypeId: 'satellite',
        tilt: 0,
      });

      // Enable drawing manager
      const drawingManager = new window.google.maps.drawing.DrawingManager({
        drawingMode: window.google.maps.drawing.OverlayType.POLYGON,
        drawingControl: true,
        drawingControlOptions: {
          position: window.google.maps.ControlPosition.TOP_CENTER,
          drawingModes: [window.google.maps.drawing.OverlayType.POLYGON],
        },
        polygonOptions: {
          fillColor: '#FF6B35',
          fillOpacity: 0.5,
          strokeColor: '#FF6B35',
          strokeWeight: 2,
          editable: true,
          draggable: true,
        },
      });

      drawingManager.setMap(newMap);

      // Handle polygon completion
      drawingManager.addListener('polygoncomplete', (newPolygon) => {
        // Remove previous polygon if exists
        if (polygon) {
          polygon.setMap(null);
        }

        setPolygon(newPolygon);
        calculateArea(newPolygon);

        // Add listener for polygon changes
        newPolygon.getPath().addListener('set_at', () => calculateArea(newPolygon));
        newPolygon.getPath().addListener('insert_at', () => calculateArea(newPolygon));
        newPolygon.getPath().addListener('remove_at', () => calculateArea(newPolygon));

        // Disable drawing after polygon is created
        drawingManager.setDrawingMode(null);
      });

      setMap(newMap);
    }
  }, [mapRef.current, map, userLocation]);

  const calculateArea = (polygon) => {
    const path = polygon.getPath();
    const areaInSquareMeters = window.google.maps.geometry.spherical.computeArea(path);
    setArea(areaInSquareMeters);
    onAreaChange(areaInSquareMeters);
  };

  const clearPolygon = () => {
    if (polygon) {
      polygon.setMap(null);
      setPolygon(null);
      setArea(0);
      onAreaChange(0);
    }
  };

  return (
    <div className="google-maps-container">
      <div ref={mapRef} style={{ height: '400px', width: '100%' }} />
      <div className="map-controls">
        <button onClick={clearPolygon} className="btn btn-secondary" disabled={!polygon}>
          Clear Roof Area
        </button>
        {area > 0 && (
          <div className="area-display">
            <strong>Roof Area: {area.toFixed(2)} mÂ²</strong>
          </div>
        )}
      </div>
      <div className="map-instructions">
        <p>
          <strong>Instructions:</strong>
          <br />
          1. Use the polygon tool to trace your roof outline
          <br />
          2. Click to add points around your roof perimeter
          <br />
          3. Double-click to complete the polygon
          <br />
          4. Drag the points to adjust the shape
        </p>
      </div>
    </div>
  );
};

const render = (status) => {
  switch (status) {
    case Status.LOADING:
      return <div>Loading Google Maps...</div>;
    case Status.FAILURE:
      return <div>Error loading Google Maps</div>;
    case Status.SUCCESS:
      return null;
  }
};

const RoofCondition = ({ data, onDataChange, userLocation }) => {
  const [formData, setFormData] = useState({
    roofType: data.roofType || "",
    roofOrientation: data.roofOrientation || "",
    roofAngle: data.roofAngle || "",
    roofArea: data.roofArea || "",
    hasShading: data.hasShading || "",
    useMapsForArea: data.useMapsForArea || false,
    ...data
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const newValue = type === 'checkbox' ? checked : value;

    setFormData(prev => ({
      ...prev,
      [name]: newValue
    }));
    onDataChange({ [name]: newValue });
  };

  const handleAreaChange = (areaFromMaps) => {
    const newFormData = {
      ...formData,
      roofArea: areaFromMaps.toFixed(2)
    };
    setFormData(newFormData);
    onDataChange({ roofArea: areaFromMaps.toFixed(2) });
  };

  const apiKey = "AIzaSyCpAABUQR_MiKb4yDovC3-1jM0LdIs59Hc";

  return (
    <div className="roof-condition-step">
      <div className="step-header">
        <h3>2.1 Roof Condition</h3>
        <p>Provide details about your roof configuration and solar potential.</p>
      </div>

      <div className="form-group">
        <label>Roof Type *</label>
        <div className="radio-group">
          {[
            { value: "pitched", label: "Pitched Roof" },
            { value: "flat", label: "Flat Roof" },
            { value: "mixed", label: "Mixed" }
          ].map(option => (
            <div
              key={option.value}
              className={`radio-option ${formData.roofType === option.value ? 'selected' : ''}`}
              onClick={() => handleChange({ target: { name: 'roofType', value: option.value } })}
            >
              <input
                type="radio"
                name="roofType"
                value={option.value}
                checked={formData.roofType === option.value}
                onChange={handleChange}
              />
              <span>{option.label}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="form-group">
        <label>Primary Roof Orientation *</label>
        <div className="radio-group">
          {[
            { value: "south", label: "South" },
            { value: "east", label: "East" },
            { value: "west", label: "West" },
            { value: "north", label: "North" }
          ].map(option => (
            <div
              key={option.value}
              className={`radio-option ${formData.roofOrientation === option.value ? 'selected' : ''}`}
              onClick={() => handleChange({ target: { name: 'roofOrientation', value: option.value } })}
            >
              <input
                type="radio"
                name="roofOrientation"
                value={option.value}
                checked={formData.roofOrientation === option.value}
                onChange={handleChange}
              />
              <span>{option.label}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="form-group">
        <label htmlFor="roofAngle">Roof Angle (degrees)</label>
        <input
          type="number"
          id="roofAngle"
          name="roofAngle"
          value={formData.roofAngle}
          onChange={handleChange}
          placeholder="e.g., 30"
          min="0"
          max="90"
        />
        <small>Leave blank if unknown - we'll estimate based on typical values</small>
      </div>

      <div className="form-group">
        <div className="checkbox-group">
          <label className="checkbox-label">
            <input
              type="checkbox"
              name="useMapsForArea"
              checked={formData.useMapsForArea}
              onChange={handleChange}
            />
            <span className="checkmark"></span>
            Use Google Maps to precisely measure roof area
          </label>
        </div>
      </div>

      {formData.useMapsForArea ? (
        <div className="form-group">
          <label>Roof Area Measurement</label>
          <Wrapper apiKey={apiKey} render={render} libraries={['drawing', 'geometry']}>
            <GoogleMapsPolygonDrawer 
              onAreaChange={handleAreaChange}
              userLocation={userLocation}
              apiKey={apiKey}
            />
          </Wrapper>
        </div>
      ) : (
        <div className="form-group">
          <label htmlFor="roofArea">Available Roof Area (square meters)</label>
          <input
            type="number"
            id="roofArea"
            name="roofArea"
            value={formData.roofArea}
            onChange={handleChange}
            placeholder="e.g., 50"
            min="0"
          />
          <small>Approximate area suitable for solar panels</small>
        </div>
      )}

      <div className="form-group">
        <label>Roof Shading *</label>
        <div className="radio-group">
          {[
            { value: "none", label: "No Shading" },
            { value: "partial", label: "Partial Shading" },
            { value: "significant", label: "Significant Shading" }
          ].map(option => (
            <div
              key={option.value}
              className={`radio-option ${formData.hasShading === option.value ? 'selected' : ''}`}
              onClick={() => handleChange({ target: { name: 'hasShading', value: option.value } })}
            >
              <input
                type="radio"
                name="hasShading"
                value={option.value}
                checked={formData.hasShading === option.value}
                onChange={handleChange}
              />
              <span>{option.label}</span>
            </div>
          ))}
        </div>
        <small>Consider shading from trees, buildings, or other structures that might affect solar panels</small>
      </div>
    </div>
  );
};

export default RoofCondition;
