import React, { useState } from "react";

const SystemConfiguration = () => {
  const [selectedEquipment, setSelectedEquipment] = useState({
    solarPanels: false,
    batteryStorage: false,
    heatPump: false
  });
  const [configurationConfirmed, setConfigurationConfirmed] = useState(false);

  // Equipment selection handler
  const handleEquipmentChange = (equipment) => {
    setSelectedEquipment(prev => ({
      ...prev,
      [equipment]: !prev[equipment]
    }));
    setConfigurationConfirmed(false); // Reset confirmation when selection changes
  };

  // Confirm configuration handler
  const handleConfirmConfiguration = () => {
    setConfigurationConfirmed(true);
  };

  // Check if any equipment is selected
  const hasSelectedEquipment = Object.values(selectedEquipment).some(selected => selected);

  return (
    <div className="system-configuration-tab">
      <div className="results-section">
        <div className="configuration-card">
          <h3>System Configuration & Comparison</h3>

          {!configurationConfirmed ? (
            /* Equipment Selection */
            <div className="equipment-selection">
              <p>What renewable energy systems are you planning to install?</p>

              <div className="equipment-options">
                <label className="equipment-checkbox">
                  <input
                    type="checkbox"
                    checked={selectedEquipment.solarPanels}
                    onChange={() => handleEquipmentChange('solarPanels')}
                  />
                  <span className="checkmark"></span>
                  <span className="equipment-label">Solar Panels</span>
                </label>

                <label className="equipment-checkbox">
                  <input
                    type="checkbox"
                    checked={selectedEquipment.batteryStorage}
                    onChange={() => handleEquipmentChange('batteryStorage')}
                  />
                  <span className="checkmark"></span>
                  <span className="equipment-label">Battery Storage</span>
                </label>

                <label className="equipment-checkbox">
                  <input
                    type="checkbox"
                    checked={selectedEquipment.heatPump}
                    onChange={() => handleEquipmentChange('heatPump')}
                  />
                  <span className="checkmark"></span>
                  <span className="equipment-label">Heat Pump</span>
                </label>
              </div>

              <button 
                className={`btn btn-primary ${!hasSelectedEquipment ? 'disabled' : ''}`}
                onClick={handleConfirmConfiguration}
                disabled={!hasSelectedEquipment}
              >
                Analyze Selected Systems
              </button>
            </div>
          ) : (
            /* Configuration Results */
            <div className="configuration-results">
              <div className="selected-systems-summary">
                <h4>Selected Systems Analysis</h4>
                <div className="selected-tags">
                  {selectedEquipment.solarPanels && <span className="system-tag solar">Solar Panels</span>}
                  {selectedEquipment.batteryStorage && <span className="system-tag battery">Battery Storage</span>}
                  {selectedEquipment.heatPump && <span className="system-tag heatpump">Heat Pump</span>}
                </div>
              </div>

              <div className="analysis-placeholder">
                <p>ðŸ”„ Analyzing your selected system configuration...</p>
                <p>Detailed analysis and recommendations will be displayed here.</p>
              </div>

              <button 
                className="btn btn-secondary"
                onClick={() => setConfigurationConfirmed(false)}
              >
                Change Selection
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SystemConfiguration;
