import React, { useState } from "react";
import RoofCondition from "./RoofCondition";
import InsulationPerformance from "./InsulationPerformance";

const HouseDetails = ({ data, onDataChange, onNext, onBack }) => {
  const [currentSubStep, setCurrentSubStep] = useState(1);
  const [formData, setFormData] = useState({
    // Roof condition data - now only roof area
    roofArea: data.roofArea || "",
    useMapsForArea: data.useMapsForArea || true, // Default to true since it's the only option
    // Insulation performance data
    insulationQuality: data.insulationQuality || "",
    windowType: data.windowType || "",
    bedrooms: data.bedrooms || "",
    mainWallType: data.mainWallType || "",
    ...data
  });

  // Get user location from previous step data for Google Maps
  const userLocation = data.latitude && data.longitude ? {
    lat: parseFloat(data.latitude),
    lng: parseFloat(data.longitude)
  } : null;

  const handleDataChange = (newData) => {
    const updatedData = {
      ...formData,
      ...newData
    };
    setFormData(updatedData);
    onDataChange(newData);
  };

  const handleNext = () => {
    if (currentSubStep === 1) {
      // Check if step 2.1 is complete (only roof area required)
      if (isStep1Valid()) {
        setCurrentSubStep(2);
      }
    } else {
      // Check if step 2.2 is complete and proceed to next main step
      if (isStep2Valid()) {
        onNext();
      }
    }
  };

  const handleBack = () => {
    if (currentSubStep === 1) {
      onBack();
    } else {
      setCurrentSubStep(1);
    }
  };

  const isStep1Valid = () => {
    // Only require roof area to be measured (greater than 0)
    return formData.roofArea && parseFloat(formData.roofArea) > 0;
  };

  const isStep2Valid = () => {
    return formData.insulationQuality;
  };

  const getProgressPercentage = () => {
    if (currentSubStep === 1) {
      return isStep1Valid() ? 50 : 25;
    } else {
      return isStep2Valid() ? 100 : 75;
    }
  };

  return (
    <div className="step-container house-details-step">
      <div className="step-header">
        <h2>House Details</h2>
        <p>Measure your roof area and tell us about your home's insulation for accurate renewable energy calculations.</p>

        {/* Sub-step navigation */}
        <div className="sub-step-navigation">
          <div className="sub-step-progress">
            <div className="progress-bar">
              <div 
                className="progress-fill" 
                style={{ width: `${getProgressPercentage()}%` }}
              ></div>
            </div>
            <div className="sub-step-labels">
              <span className={`sub-step-label ${currentSubStep === 1 ? 'active' : ''} ${isStep1Valid() ? 'completed' : ''}`}>
                2.1 Roof Area Measurement
              </span>
              <span className={`sub-step-label ${currentSubStep === 2 ? 'active' : ''} ${isStep2Valid() ? 'completed' : ''}`}>
                2.2 Insulation Performance
              </span>
            </div>
          </div>
        </div>
      </div>

      <div className="sub-step-content">
        {currentSubStep === 1 && (
          <RoofCondition 
            data={formData}
            onDataChange={handleDataChange}
            userLocation={userLocation}
          />
        )}

        {currentSubStep === 2 && (
          <InsulationPerformance 
            data={formData}
            onDataChange={handleDataChange}
          />
        )}
      </div>

      <div className="button-group">
        <button 
          type="button" 
          className="btn btn-secondary"
          onClick={handleBack}
        >
          {currentSubStep === 1 ? 'Back' : 'Back to Roof Measurement'}
        </button>
        <button 
          type="button" 
          className={`btn btn-primary ${
            (currentSubStep === 1 && !isStep1Valid()) || 
            (currentSubStep === 2 && !isStep2Valid()) ? 'disabled' : ''
          }`}
          onClick={handleNext}
          disabled={
            (currentSubStep === 1 && !isStep1Valid()) || 
            (currentSubStep === 2 && !isStep2Valid())
          }
        >
          {currentSubStep === 1 ? 'Next: Insulation Performance' : 'Next: Existing Systems'}
        </button>
      </div>
    </div>
  );
};

export default HouseDetails;
