import React, { useState, useEffect, useRef } from 'react';
import PostcodeLocationInput from './PostcodeLocationInput';

const Compass = () => {
  const directions = [
    { label: 'N', angle: 0, fullName: 'North' },
    { label: 'NNE', angle: 30, fullName: 'North-Northeast' },
    { label: 'NE', angle: 60, fullName: 'Northeast' },
    { label: 'ENE', angle: 90, fullName: 'East-Northeast' },
    { label: 'E', angle: 120, fullName: 'East' },
    { label: 'ESE', angle: 150, fullName: 'East-Southeast' },
    { label: 'SE', angle: 180, fullName: 'Southeast' },
    { label: 'SSE', angle: 210, fullName: 'South-Southeast' },
    { label: 'S', angle: 240, fullName: 'South' },
    { label: 'SSW', angle: 270, fullName: 'South-Southwest' },
    { label: 'SW', angle: 300, fullName: 'Southwest' },
    { label: 'WSW', angle: 330, fullName: 'West-Southwest' },
    { label: 'W', angle: 360, fullName: 'West' },
    { label: 'WNW', angle: 30, fullName: 'West-Northwest' },
    { label: 'NW', angle: 60, fullName: 'Northwest' },
    { label: 'NNW', angle: 90, fullName: 'North-Northwest' }
  ];

  // Correct the direction angles (0° = North, clockwise)
  const correctedDirections = [
    { label: 'N', angle: 0, fullName: 'North' },
    { label: 'NNE', angle: 22.5, fullName: 'North-Northeast' },
    { label: 'NE', angle: 45, fullName: 'Northeast' },
    { label: 'ENE', angle: 67.5, fullName: 'East-Northeast' },
    { label: 'E', angle: 90, fullName: 'East' },
    { label: 'ESE', angle: 112.5, fullName: 'East-Southeast' },
    { label: 'SE', angle: 135, fullName: 'Southeast' },
    { label: 'SSE', angle: 157.5, fullName: 'South-Southeast' },
    { label: 'S', angle: 180, fullName: 'South' },
    { label: 'SSW', angle: 202.5, fullName: 'South-Southwest' },
    { label: 'SW', angle: 225, fullName: 'Southwest' },
    { label: 'WSW', angle: 247.5, fullName: 'West-Southwest' },
    { label: 'W', angle: 270, fullName: 'West' },
    { label: 'WNW', angle: 292.5, fullName: 'West-Northwest' },
    { label: 'NW', angle: 315, fullName: 'Northwest' },
    { label: 'NNW', angle: 337.5, fullName: 'North-Northwest' }
  ];

  return (
    <div className="compass-container">
      <div className="compass">
        <div className="compass-center">
          <div className="compass-needle"></div>
        </div>
        {correctedDirections.map((direction, index) => {
          const radius = 45; // Distance from center
          const x = Math.sin((direction.angle * Math.PI) / 180) * radius;
          const y = -Math.cos((direction.angle * Math.PI) / 180) * radius;

          return (
            <div
              key={index}
              className={`compass-direction ${direction.label === 'N' || direction.label === 'S' || direction.label === 'E' || direction.label === 'W' ? 'cardinal' : 'intermediate'}`}
              style={{
                transform: `translate(${x}px, ${y}px)`,
              }}
              title={direction.fullName}
            >
              {direction.label}
            </div>
          );
        })}
      </div>
      <div className="compass-label">
        <span>Compass</span>
        <small>Click directions to see full names</small>
      </div>
    </div>
  );
};

const GoogleMapsPolygonDrawer = ({ onAreaChange, userLocation, apiKey }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [polygon, setPolygon] = useState(null);
  const [area, setArea] = useState(0);
  const [currentLocation, setCurrentLocation] = useState(userLocation);

  // Update map center when location changes
  useEffect(() => {
    if (map && currentLocation) {
      map.setCenter(currentLocation);
      map.setZoom(20);
    }
  }, [currentLocation, map]);

  useEffect(() => {
    if (mapRef.current && !map) {
      const newMap = new window.google.maps.Map(mapRef.current, {
        center: currentLocation || { lat: 51.5074, lng: -0.1278 }, // London default
        zoom: 20,
        mapTypeId: 'satellite',
        tilt: 0,
      });

      // Enable drawing manager
      const drawingManager = new window.google.maps.drawing.DrawingManager({
        drawingMode: window.google.maps.drawing.OverlayType.POLYGON,
        drawingControl: true,
        drawingControlOptions: {
          position: window.google.maps.ControlPosition.TOP_CENTER,
          drawingModes: [window.google.maps.drawing.OverlayType.POLYGON],
        },
        polygonOptions: {
          fillColor: '#FF6B35',
          fillOpacity: 0.5,
          strokeColor: '#FF6B35',
          strokeWeight: 2,
          editable: true,
          draggable: true,
        },
      });

      drawingManager.setMap(newMap);

      // Handle polygon completion
      drawingManager.addListener('polygoncomplete', (newPolygon) => {
        // Remove previous polygon if exists
        if (polygon) {
          polygon.setMap(null);
        }

        setPolygon(newPolygon);
        calculateArea(newPolygon);

        // Add listener for polygon changes
        newPolygon.getPath().addListener('set_at', () => calculateArea(newPolygon));
        newPolygon.getPath().addListener('insert_at', () => calculateArea(newPolygon));
        newPolygon.getPath().addListener('remove_at', () => calculateArea(newPolygon));

        // Disable drawing after polygon is created
        drawingManager.setDrawingMode(null);
      });

      setMap(newMap);
    }
  }, [mapRef.current, map, currentLocation]);

  const calculateArea = (polygon) => {
    const path = polygon.getPath();
    const areaInSquareMeters = window.google.maps.geometry.spherical.computeArea(path);
    setArea(areaInSquareMeters);
    onAreaChange(areaInSquareMeters);
  };

  const clearPolygon = () => {
    if (polygon) {
      polygon.setMap(null);
      setPolygon(null);
      setArea(0);
      onAreaChange(0);
    }
  };

  const enableDrawing = () => {
    if (map) {
      const drawingManager = new window.google.maps.drawing.DrawingManager({
        drawingMode: window.google.maps.drawing.OverlayType.POLYGON,
        drawingControl: true,
        drawingControlOptions: {
          position: window.google.maps.ControlPosition.TOP_CENTER,
          drawingModes: [window.google.maps.drawing.OverlayType.POLYGON],
        },
        polygonOptions: {
          fillColor: '#FF6B35',
          fillOpacity: 0.5,
          strokeColor: '#FF6B35',
          strokeWeight: 2,
          editable: true,
          draggable: true,
        },
      });
      drawingManager.setMap(map);
    }
  };

  const handleLocationChange = (newLocation) => {
    setCurrentLocation(newLocation);
    // Clear existing polygon when location changes
    if (polygon) {
      clearPolygon();
    }
  };

  return (
    <div className="google-maps-container">
      <div className="map-location-controls">
        <h5>Change Location</h5>
        <p>Enter a different postcode to locate another property:</p>
        <PostcodeLocationInput onLocationChange={handleLocationChange} />
      </div>

      <div className="map-with-compass">
        <div ref={mapRef} style={{ height: '500px', width: '100%' }} />
        <Compass />
      </div>

      <div className="map-controls">
        <button onClick={clearPolygon} className="btn btn-secondary" disabled={!polygon}>
          Clear Roof Area
        </button>
        <button onClick={enableDrawing} className="btn btn-outline">
          Draw New Area
        </button>
      </div>
    </div>
  );
};

export default GoogleMapsPolygonDrawer;
