import React, { useState } from "react";

const EnergyDemand = ({ data, onDataChange, onNext, onBack }) => {
  const [formData, setFormData] = useState({
    householdSize: data.householdSize || "",
    knowsMonthlyUsage: data.knowsMonthlyUsage || "",
    monthlyElectricityUsage: data.monthlyElectricityUsage || {
      january: "", february: "", march: "", april: "", may: "", june: "",
      july: "", august: "", september: "", october: "", november: "", december: ""
    },
    knowsAnnualUsage: data.knowsAnnualUsage || "",
    annualElectricityUsage: data.annualElectricityUsage || "",
    floorArea: data.floorArea || "",
    ...data
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    let updatedData = { [name]: value };

    // Clear related fields when switching between monthly/annual usage options
    if (name === "knowsMonthlyUsage") {
      if (value === "yes") {
        updatedData.knowsAnnualUsage = "";
        updatedData.annualElectricityUsage = "";
      } else if (value === "no") {
        updatedData.monthlyElectricityUsage = {
          january: "", february: "", march: "", april: "", may: "", june: "",
          july: "", august: "", september: "", october: "", november: "", december: ""
        };
      }
    } else if (name === "knowsAnnualUsage" && value === "yes") {
      // Clear monthly usage when switching to knowing annual
      updatedData.monthlyElectricityUsage = {
        january: "", february: "", march: "", april: "", may: "", june: "",
        july: "", august: "", september: "", october: "", november: "", december: ""
      };
    }

    setFormData(prev => ({
      ...prev,
      ...updatedData
    }));
    onDataChange(updatedData);
  };

  const handleMonthlyChange = (month, value) => {
    const updatedMonthlyUsage = {
      ...formData.monthlyElectricityUsage,
      [month]: value
    };

    setFormData(prev => ({
      ...prev,
      monthlyElectricityUsage: updatedMonthlyUsage
    }));
    onDataChange({ monthlyElectricityUsage: updatedMonthlyUsage });
  };

  const handleNext = () => {
    onNext();
  };

  const isFormValid = () => {
    if (!formData.householdSize || !formData.knowsMonthlyUsage || !formData.floorArea) {
      return false;
    }

    if (formData.knowsMonthlyUsage === "yes") {
      // Check if all 12 months have values
      const months = ["january", "february", "march", "april", "may", "june", 
                     "july", "august", "september", "october", "november", "december"];
      return months.every(month => formData.monthlyElectricityUsage[month] && 
                                   formData.monthlyElectricityUsage[month].trim() !== "");
    }

    if (formData.knowsMonthlyUsage === "no") {
      if (!formData.knowsAnnualUsage) {
        return false;
      }
      if (formData.knowsAnnualUsage === "yes") {
        return formData.annualElectricityUsage;
      }
      return true; // If they don't know annual usage either, that's valid
    }

    return false;
  };

  return (
    <div className="step-container">
      <div className="step-header">
        <h2>Energy Demand</h2>
        <p>Tell us about your household's energy usage patterns to calculate accurate renewable energy recommendations.</p>
      </div>

      <div className="form-group">
        <label htmlFor="householdSize">How many people live in your household regularly?</label>
        <input
          type="number"
          id="householdSize"
          name="householdSize"
          value={formData.householdSize}
          onChange={handleChange}
          placeholder="Enter number of people"
          min="1"
          max="20"
          className="form-input"
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="knowsMonthlyUsage">Do you know your monthly electricity usage?</label>
        <select
          id="knowsMonthlyUsage"
          name="knowsMonthlyUsage"
          value={formData.knowsMonthlyUsage}
          onChange={handleChange}
          className="form-select"
          required
        >
          <option value="">Select an option</option>
          <option value="yes">Yes</option>
          <option value="no">No</option>
        </select>
      </div>

      {formData.knowsMonthlyUsage === "yes" && (
        <div className="form-group">
          <div className="monthly-usage-container">
            <h3 className="monthly-usage-title">Monthly electricity usage (kWh)</h3>
            <p className="monthly-usage-description">Please enter your electricity usage for each month:</p>
            <div className="monthly-usage-grid">
            {[
              { key: "january", label: "January" },
              { key: "february", label: "February" },
              { key: "march", label: "March" },
              { key: "april", label: "April" },
              { key: "may", label: "May" },
              { key: "june", label: "June" },
              { key: "july", label: "July" },
              { key: "august", label: "August" },
              { key: "september", label: "September" },
              { key: "october", label: "October" },
              { key: "november", label: "November" },
              { key: "december", label: "December" }
            ].map(month => (
              <div key={month.key} className="monthly-input-group">
                <label htmlFor={month.key} className="month-label">{month.label}</label>
                <input
                  type="number"
                  id={month.key}
                  name={month.key}
                  value={formData.monthlyElectricityUsage[month.key]}
                  onChange={(e) => handleMonthlyChange(month.key, e.target.value)}
                  placeholder="kWh"
                  min="0"
                  step="0.1"
                  className="form-input month-input"
                  required
                />
              </div>
            ))}
            </div>
          </div>
        </div>
      )}

      {formData.knowsMonthlyUsage === "no" && (
        <>
          <div className="form-group">
            <label htmlFor="knowsAnnualUsage">Do you know your annual electricity usage?</label>
            <select
              id="knowsAnnualUsage"
              name="knowsAnnualUsage"
              value={formData.knowsAnnualUsage}
              onChange={handleChange}
              className="form-select"
              required
            >
              <option value="">Select an option</option>
              <option value="yes">Yes</option>
              <option value="no">No</option>
            </select>
          </div>

          {formData.knowsAnnualUsage === "yes" && (
            <div className="form-group">
              <label htmlFor="annualElectricityUsage">Annual electricity usage (kWh)</label>
              <input
                type="number"
                id="annualElectricityUsage"
                name="annualElectricityUsage"
                value={formData.annualElectricityUsage}
                onChange={handleChange}
                placeholder="Enter annual usage in kWh"
                min="0"
                step="1"
                className="form-input"
                required
              />
            </div>
          )}
        </>
      )}

      <div className="form-group">
        <label htmlFor="floorArea">What is your approximate floor area? (mÂ²)</label>
        <input
          type="number"
          id="floorArea"
          name="floorArea"
          value={formData.floorArea}
          onChange={handleChange}
          placeholder="Enter floor area in square metres"
          min="1"
          step="0.1"
          className="form-input"
          required
        />
      </div>

      <div className="button-group">
        <button 
          type="button" 
          className="btn btn-secondary"
          onClick={onBack}
        >
          Back
        </button>
        <button 
          type="button" 
          className={`btn btn-primary ${!isFormValid() ? 'disabled' : ''}`}
          onClick={handleNext}
          disabled={!isFormValid()}
        >
          Next: Solar Configuration
        </button>
      </div>
    </div>
  );
};

export default EnergyDemand;
