import React from "react";
import BasicInformation from "./steps/BasicInformation";
import HouseDetails from "./steps/HouseDetails";
import ExistingSystems from "./steps/ExistingSystems";
import UpgradePlan from "./steps/UpgradePlan";
import Tariffs from "./steps/Tariffs";
import Results from "./steps/Results";
import DemoMode from "./DemoMode";

const MainContent = ({ 
  currentStep, 
  selectedData, 
  onDataChange, 
  onStepChange, 
  calculationResult, 
  setCalculationResult,
  showDemo 
}) => {

  if (showDemo) {
    return <DemoMode />;
  }

  const renderStep = () => {
    switch (currentStep) {
      case "basic-information":
        return (
          <BasicInformation 
            data={selectedData}
            onDataChange={onDataChange}
            onNext={(nextStep) => onStepChange(nextStep || "house-details")}
          />
        );
      case "house-details":
        return (
          <HouseDetails 
            data={selectedData}
            onDataChange={onDataChange}
            onNext={() => onStepChange("existing-systems")}
            onBack={() => onStepChange("basic-information")}
          />
        );
      case "existing-systems":
        return (
          <ExistingSystems 
            data={selectedData}
            onDataChange={onDataChange}
            onNext={() => onStepChange("upgrade-plan")}
            onBack={() => onStepChange("house-details")}
          />
        );
      case "upgrade-plan":
        return (
          <UpgradePlan 
            data={selectedData}
            onDataChange={onDataChange}
            onNext={() => onStepChange("tariffs")}
            onBack={() => onStepChange("existing-systems")}
          />
        );
      case "tariffs":
        return (
          <Tariffs 
            data={selectedData}
            onDataChange={onDataChange}
            onNext={() => onStepChange("results")}
            onBack={() => onStepChange("upgrade-plan")}
          />
        );
      case "results":
        return (
          <Results 
            data={selectedData}
            calculationResult={calculationResult}
            setCalculationResult={setCalculationResult}
            onBack={() => onStepChange("tariffs")}
          />
        );
      default:
        return <div>Unknown step</div>;
    }
  };

  return (
    <div className="main-content">
      {renderStep()}
    </div>
  );
};

export default MainContent;
