import React, { useState } from "react";

const EnergyDemand = ({ data, onDataChange, onNext, onBack }) => {
  const [formData, setFormData] = useState({
    householdSize: data.householdSize || "",
    knowsMonthlyUsage: data.knowsMonthlyUsage || "",
    monthlyElectricityUsage: data.monthlyElectricityUsage || {
      january: "", february: "", march: "", april: "", may: "", june: "",
      july: "", august: "", september: "", october: "", november: "", december: ""
    },
    knowsAnnualUsage: data.knowsAnnualUsage || "",
    annualElectricityUsage: data.annualElectricityUsage || "",
    ...data
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    let updatedData = { [name]: value };

    // Clear related fields when switching between monthly/annual usage options
    if (name === "knowsMonthlyUsage") {
      if (value === "yes") {
        updatedData.knowsAnnualUsage = "";
        updatedData.annualElectricityUsage = "";
      } else if (value === "no") {
        updatedData.monthlyElectricityUsage = "";
      }
    } else if (name === "knowsAnnualUsage" && value === "yes") {
      // Clear monthly usage when switching to knowing annual
      updatedData.monthlyElectricityUsage = "";
    }

    setFormData(prev => ({
      ...prev,
      ...updatedData
    }));
    onDataChange(updatedData);
  };

  const handleNext = () => {
    onNext();
  };

  const isFormValid = () => {
    if (!formData.householdSize || !formData.knowsMonthlyUsage) {
      return false;
    }

    if (formData.knowsMonthlyUsage === "yes") {
      return formData.monthlyElectricityUsage;
    }

    if (formData.knowsMonthlyUsage === "no") {
      if (!formData.knowsAnnualUsage) {
        return false;
      }
      if (formData.knowsAnnualUsage === "yes") {
        return formData.annualElectricityUsage;
      }
      return true; // If they don't know annual usage either, that's valid
    }

    return false;
  };

  return (
    <div className="step-container">
      <div className="step-header">
        <h2>Energy Demand</h2>
        <p>Tell us about your household's energy usage patterns to calculate accurate renewable energy recommendations.</p>
      </div>

      <div className="form-group">
        <label htmlFor="householdSize">How many people live in your household regularly?</label>
        <input
          type="number"
          id="householdSize"
          name="householdSize"
          value={formData.householdSize}
          onChange={handleChange}
          placeholder="Enter number of people"
          min="1"
          max="20"
          className="form-input"
          required
        />
      </div>

      <div className="form-group">
        <label htmlFor="knowsMonthlyUsage">Do you know your monthly electricity usage?</label>
        <select
          id="knowsMonthlyUsage"
          name="knowsMonthlyUsage"
          value={formData.knowsMonthlyUsage}
          onChange={handleChange}
          className="form-select"
          required
        >
          <option value="">Select an option</option>
          <option value="yes">Yes</option>
          <option value="no">No</option>
        </select>
      </div>

      {formData.knowsMonthlyUsage === "yes" && (
        <div className="form-group">
          <label htmlFor="monthlyElectricityUsage">Monthly electricity usage (kWh)</label>
          <input
            type="number"
            id="monthlyElectricityUsage"
            name="monthlyElectricityUsage"
            value={formData.monthlyElectricityUsage}
            onChange={handleChange}
            placeholder="Enter monthly usage in kWh"
            min="0"
            step="0.1"
            className="form-input"
            required
          />
        </div>
      )}

      {formData.knowsMonthlyUsage === "no" && (
        <>
          <div className="form-group">
            <label htmlFor="knowsAnnualUsage">Do you know your annual electricity usage?</label>
            <select
              id="knowsAnnualUsage"
              name="knowsAnnualUsage"
              value={formData.knowsAnnualUsage}
              onChange={handleChange}
              className="form-select"
              required
            >
              <option value="">Select an option</option>
              <option value="yes">Yes</option>
              <option value="no">No</option>
            </select>
          </div>

          {formData.knowsAnnualUsage === "yes" && (
            <div className="form-group">
              <label htmlFor="annualElectricityUsage">Annual electricity usage (kWh)</label>
              <input
                type="number"
                id="annualElectricityUsage"
                name="annualElectricityUsage"
                value={formData.annualElectricityUsage}
                onChange={handleChange}
                placeholder="Enter annual usage in kWh"
                min="0"
                step="1"
                className="form-input"
                required
              />
            </div>
          )}
        </>
      )}

      <div className="button-group">
        <button 
          type="button" 
          className="btn btn-secondary"
          onClick={onBack}
        >
          Back
        </button>
        <button 
          type="button" 
          className={`btn btn-primary ${!isFormValid() ? 'disabled' : ''}`}
          onClick={handleNext}
          disabled={!isFormValid()}
        >
          Next: Solar Configuration
        </button>
      </div>
    </div>
  );
};

export default EnergyDemand;
