import React, { useState } from "react";

const HouseDetails = ({ data, onDataChange, onNext, onBack }) => {
  const [formData, setFormData] = useState({
    roofType: data.roofType || "",
    roofOrientation: data.roofOrientation || "",
    roofAngle: data.roofAngle || "",
    roofArea: data.roofArea || "",
    insulationQuality: data.insulationQuality || "",
    windowType: data.windowType || "",
    hasShading: data.hasShading || "",
    bedrooms: data.bedrooms || "",
    mainWallType: data.mainWallType || "",
    ...data
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    onDataChange({ [name]: value });
  };

  const handleNext = () => {
    if (formData.roofType && formData.roofOrientation && formData.insulationQuality && formData.hasShading) {
      onNext();
    }
  };

  const isFormValid = formData.roofType && formData.roofOrientation && formData.insulationQuality && formData.hasShading;

  return (
    <div className="step-container">
      <div className="step-header">
        <h2>House Details</h2>
        <p>Tell us about your roof and insulation to calculate solar potential and energy efficiency.</p>
      </div>

      <div className="form-group">
        <label>Roof Type *</label>
        <div className="radio-group">
          {[
            { value: "pitched", label: "Pitched Roof" },
            { value: "flat", label: "Flat Roof" },
            { value: "mixed", label: "Mixed" }
          ].map(option => (
            <div
              key={option.value}
              className={`radio-option ${formData.roofType === option.value ? 'selected' : ''}`}
              onClick={() => handleChange({ target: { name: 'roofType', value: option.value } })}
            >
              <input
                type="radio"
                name="roofType"
                value={option.value}
                checked={formData.roofType === option.value}
                onChange={handleChange}
              />
              <span>{option.label}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="form-group">
        <label>Primary Roof Orientation *</label>
        <div className="radio-group">
          {[
            { value: "south", label: "South" },
            { value: "east", label: "East" },
            { value: "west", label: "West" },
            { value: "north", label: "North" }
          ].map(option => (
            <div
              key={option.value}
              className={`radio-option ${formData.roofOrientation === option.value ? 'selected' : ''}`}
              onClick={() => handleChange({ target: { name: 'roofOrientation', value: option.value } })}
            >
              <input
                type="radio"
                name="roofOrientation"
                value={option.value}
                checked={formData.roofOrientation === option.value}
                onChange={handleChange}
              />
              <span>{option.label}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="form-group">
        <label htmlFor="roofAngle">Roof Angle (degrees)</label>
        <input
          type="number"
          id="roofAngle"
          name="roofAngle"
          value={formData.roofAngle}
          onChange={handleChange}
          placeholder="e.g., 30"
          min="0"
          max="90"
        />
        <small>Leave blank if unknown - we'll estimate based on typical values</small>
      </div>

      <div className="form-group">
        <label htmlFor="roofArea">Available Roof Area (square meters)</label>
        <input
          type="number"
          id="roofArea"
          name="roofArea"
          value={formData.roofArea}
          onChange={handleChange}
          placeholder="e.g., 50"
          min="0"
        />
        <small>Approximate area suitable for solar panels</small>
      </div>

      <div className="form-group">
        <label htmlFor="insulationQuality">Insulation Quality *</label>
        <select
          id="insulationQuality"
          name="insulationQuality"
          value={formData.insulationQuality}
          onChange={handleChange}
          required
        >
          <option value="">Select insulation quality</option>
          <option value="poor">Poor (minimal/old insulation)</option>
          <option value="fair">Fair (some insulation)</option>
          <option value="good">Good (well insulated)</option>
          <option value="excellent">Excellent (modern standards)</option>
        </select>
      </div>

      <div className="form-group">
        <label htmlFor="windowType">Window Type</label>
        <select
          id="windowType"
          name="windowType"
          value={formData.windowType}
          onChange={handleChange}
        >
          <option value="">Select window type</option>
          <option value="single">Single glazed</option>
          <option value="double">Double glazed</option>
          <option value="triple">Triple glazed</option>
          <option value="mixed">Mixed</option>
        </select>
      </div>

      <div className="form-group">
        <label>Roof Shading *</label>
        <div className="radio-group">
          {[
            { value: "none", label: "No Shading" },
            { value: "partial", label: "Partial Shading" },
            { value: "significant", label: "Significant Shading" }
          ].map(option => (
            <div
              key={option.value}
              className={`radio-option ${formData.hasShading === option.value ? 'selected' : ''}`}
              onClick={() => handleChange({ target: { name: 'hasShading', value: option.value } })}
            >
              <input
                type="radio"
                name="hasShading"
                value={option.value}
                checked={formData.hasShading === option.value}
                onChange={handleChange}
              />
              <span>{option.label}</span>
            </div>
          ))}
        </div>
        <small>Consider shading from trees, buildings, or other structures that might affect solar panels</small>
      </div>

      <div className="form-group">
        <label htmlFor="bedrooms">Number of Bedrooms</label>
        <select
          id="bedrooms"
          name="bedrooms"
          value={formData.bedrooms}
          onChange={handleChange}
        >
          <option value="">Select number of bedrooms</option>
          <option value="1">1 bedroom</option>
          <option value="2">2 bedrooms</option>
          <option value="3">3 bedrooms</option>
          <option value="4">4 bedrooms</option>
          <option value="5">5 bedrooms</option>
          <option value="6+">6+ bedrooms</option>
        </select>
        <small>This helps estimate your household's energy consumption patterns</small>
      </div>

      <div className="form-group">
        <label htmlFor="mainWallType">Main Wall Type</label>
        <select
          id="mainWallType"
          name="mainWallType"
          value={formData.mainWallType}
          onChange={handleChange}
        >
          <option value="">Select main wall type</option>
          <option value="solid-brick">Solid brick wall</option>
          <option value="solid-stone">Solid stone wall</option>
          <option value="cavity-insulated">Cavity wall, I know it has been insulated</option>
          <option value="cavity-not-insulated">Cavity wall, I know it has not been insulated</option>
          <option value="cavity-unknown">Cavity wall, I don't know if it has been insulated</option>
          <option value="timber-frame">Timber frame wall</option>
          <option value="concrete-construction">Concrete construction wall</option>
          <option value="unknown-construction">I don't know the wall construction</option>
        </select>
        <small>Wall type affects heat loss and insulation potential for energy efficiency calculations</small>
      </div>

      <div className="button-group">
        <button 
          type="button" 
          className="btn btn-secondary"
          onClick={onBack}
        >
          Back
        </button>
        <button 
          type="button" 
          className={`btn btn-primary ${!isFormValid ? 'disabled' : ''}`}
          onClick={handleNext}
          disabled={!isFormValid}
        >
          Next: Existing Systems
        </button>
      </div>
    </div>
  );
};

export default HouseDetails;
