import React, { useState } from 'react';
import PostcodeInput from './address-input/PostcodeInput';
import LocationInput from './address-input/LocationInput';
import CitySelector from './address-input/CitySelector';
import ClimateChart from './address-input/ClimateChart';

const BasicInformation = ({ data, onDataChange, onNext }) => {
  const [activeMethod, setActiveMethod] = useState('postcode');
  const [locationData, setLocationData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showHelp, setShowHelp] = useState(false);

  // 新增：房产信息状态
  const [propertyInfo, setPropertyInfo] = useState({
    propertyType: '',
    buildingYear: '',
    epcRating: ''
  });

  // 新增：控制各个问题的帮助提示
  const [activeTooltip, setActiveTooltip] = useState(null);

  const handleLocationSelect = (location) => {
    setLocationData(location);
    // 保存位置数据到父组件
    const updatedData = {
      ...propertyInfo, // 包含房产信息
      locationMethod: location.type,
      postcode: location.postcode,
      latitude: location.latitude,
      longitude: location.longitude,
      location: location.location,
      country: location.country,
      city: location.city,
      region: location.region
    };
    onDataChange(updatedData);
  };

  // 新增：处理房产信息变更
  const handlePropertyInfoChange = (field, value) => {
    const updatedPropertyInfo = { ...propertyInfo, [field]: value };
    setPropertyInfo(updatedPropertyInfo);

    // 如果位置数据存在，同时更新父组件数据
    if (locationData) {
      const updatedData = {
        ...updatedPropertyInfo,
        locationMethod: locationData.type,
        postcode: locationData.postcode,
        latitude: locationData.latitude,
        longitude: locationData.longitude,
        location: locationData.location,
        country: locationData.country,
        city: locationData.city,
        region: locationData.region
      };
      onDataChange(updatedData);
    }
  };

  // 新增：显示/隐藏帮助提示
  const toggleTooltip = (field) => {
    setActiveTooltip(activeTooltip === field ? null : field);
  };

  // 新增：帮助内容定义
  const helpContent = {
    propertyType: {
      title: "Property Type Impact",
      content: [
        "Different property types have varying energy characteristics:",
        "",
        "• Detached: Higher heat loss due to more external walls and roof area. More space for solar panels but higher heating needs.",
        "",
        "• Semi-detached: Moderate efficiency with one shared wall reducing heat loss. Good balance of efficiency and solar potential.",
        "",
        "• Terraced: Better efficiency with two shared walls reducing heat loss significantly. Limited roof space but lower energy needs.",
        "",
        "• End-terraced: Good efficiency with one shared wall. More external wall area than mid-terrace but better than detached.",
        "",
        "This affects heating costs, solar panel placement options, and heat pump sizing requirements for your property."
      ]
    },
    buildingYear: {
      title: "Building Year Impact", 
      content: [
        "Construction period determines building standards and efficiency:",
        "",
        "• Before 1930: Typically poor insulation, single glazing, solid walls. Higher energy consumption but often larger roof areas.",
        "",
        "• 1930-1980: Basic insulation standards, some double glazing introduced. Moderate efficiency with cavity walls becoming common.",
        "",
        "• 1981-2002: Improved building standards, better insulation requirements, double glazing standard.",
        "",
        "• After 2003: Modern building regulations with good thermal efficiency, proper insulation, and energy-efficient windows.",
        "",
        "Newer properties typically need smaller heating systems and achieve better renewable energy performance with faster payback periods."
      ]
    },
    epcRating: {
      title: "EPC Rating Impact",
      content: [
        "Energy Performance Certificate rating directly affects your renewable energy potential:",
        "",
        "• A-B Rating: Excellent efficiency, very low energy needs. Smaller renewable systems required, excellent payback.",
        "",
        "• C-D Rating: Good to average efficiency. Moderate energy consumption, good renewable energy potential.",
        "",
        "• E-F Rating: Poor efficiency, higher energy consumption. Larger systems needed but greater potential savings.",
        "",
        "• G Rating: Very poor efficiency, very high energy use. Maximum benefit from efficiency improvements and renewables.",
        "",
        "Better ratings mean lower heating bills, smaller renewable systems needed, and faster payback on green technology investments."
      ]
    }
  };

  // 新增：渲染帮助弹窗
  const renderFieldHelpModal = () => {
    if (!activeTooltip) return null;

    const help = helpContent[activeTooltip];

    return (
      <div className="field-help-overlay" onClick={() => setActiveTooltip(null)}>
        <div className="field-help-modal" onClick={(e) => e.stopPropagation()}>
          <div className="field-help-header">
            <h3>{help.title}</h3>
            <button 
              className="field-help-close-btn" 
              onClick={() => setActiveTooltip(null)}
              aria-label="Close help"
            >
              ×
            </button>
          </div>

          <div className="field-help-content">
            {help.content.map((line, index) => (
              line === "" ? <br key={index} /> : <p key={index}>{line}</p>
            ))}
          </div>
        </div>
      </div>
    );
  };

  const handleMethodChange = (method) => {
    setActiveMethod(method);
    setLocationData(null); // 清除之前的数据
  };

  const handleNext = () => {
    // 检查位置数据和房产信息是否都已完成
    if (locationData && propertyInfo.propertyType && propertyInfo.buildingYear && propertyInfo.epcRating) {
      onNext();
    }
  };

  // 新增：渲染房产信息表单
  const renderPropertyInformation = () => {
    if (!locationData) return null; // 只有选择了位置后才显示

    return (
      <div className="property-information">
        <h3>Basic Property Information</h3>
        <p>Tell us about your property to get more accurate recommendations.</p>

        <div className="property-form">
          <div className="form-group">
            <div className="form-label-with-help">
              <label htmlFor="propertyType">Property Type</label>
              <button 
                className="field-help-btn"
                onClick={() => toggleTooltip('propertyType')}
                type="button"
                aria-label="Get help about property type"
              >
                ?
              </button>
            </div>
            <select
              id="propertyType"
              value={propertyInfo.propertyType}
              onChange={(e) => handlePropertyInfoChange('propertyType', e.target.value)}
              className="form-select"
            >
              <option value="">Select property type</option>
              <option value="detached">Detached</option>
              <option value="semi-detached">Semi-detached</option>
              <option value="terraced">Terraced</option>
              <option value="end-terraced">End-terraced</option>
            </select>
          </div>

          <div className="form-group">
            <div className="form-label-with-help">
              <label htmlFor="buildingYear">Building Year</label>
              <button 
                className="field-help-btn"
                onClick={() => toggleTooltip('buildingYear')}
                type="button"
                aria-label="Get help about building year"
              >
                ?
              </button>
            </div>
            <select
              id="buildingYear"
              value={propertyInfo.buildingYear}
              onChange={(e) => handlePropertyInfoChange('buildingYear', e.target.value)}
              className="form-select"
            >
              <option value="">Select building year</option>
              <option value="before-1930">Before 1930</option>
              <option value="1930-1980">1930-1980</option>
              <option value="1981-2002">1981-2002</option>
              <option value="after-2003">After 2003 (if unsure, choose this)</option>
            </select>
          </div>

          <div className="form-group">
            <div className="form-label-with-help">
              <label htmlFor="epcRating">EPC Rating</label>
              <button 
                className="field-help-btn"
                onClick={() => toggleTooltip('epcRating')}
                type="button"
                aria-label="Get help about EPC rating"
              >
                ?
              </button>
            </div>
            <select
              id="epcRating"
              value={propertyInfo.epcRating}
              onChange={(e) => handlePropertyInfoChange('epcRating', e.target.value)}
              className="form-select"
            >
              <option value="">Select EPC rating</option>
              <option value="A">A</option>
              <option value="B">B</option>
              <option value="C">C</option>
              <option value="D">D (if unsure, choose this - UK average)</option>
              <option value="E">E</option>
              <option value="F">F</option>
              <option value="G">G</option>
            </select>
            <small className="form-helper">
              <a 
                href="https://www.gov.uk/find-energy-certificate" 
                target="_blank" 
                rel="noopener noreferrer"
                className="helper-link"
              >
                Find my property's energy certificate
              </a>
            </small>
          </div>
        </div>
      </div>
    );
  };

  // 新增：帮助弹窗组件
  const renderHelpModal = () => {
    if (!showHelp) return null;

    return (
      <div className="help-modal-overlay" onClick={() => setShowHelp(false)}>
        <div className="help-modal" onClick={(e) => e.stopPropagation()}>
          <div className="help-header">
            <h3>Location Information Help</h3>
            <button 
              className="help-close-btn" 
              onClick={() => setShowHelp(false)}
              aria-label="Close help"
            >
              ×
            </button>
          </div>

          <div className="help-content">
            <div className="help-section">
              <h4>Why do we need your location?</h4>
              <p>Your location is essential for calculating accurate renewable energy potential. We use it to:</p>
              <ul>
                <li><strong>Solar radiation data:</strong> Get historical solar irradiance data from NASA satellites for your exact location</li>
                <li><strong>Weather patterns:</strong> Retrieve 5-year average temperature, wind, and precipitation data</li>
                <li><strong>Seasonal variations:</strong> Understand how solar and heat pump performance varies throughout the year</li>
                <li><strong>Local climate factors:</strong> Account for regional differences in renewable energy potential</li>
              </ul>
            </div>

            <div className="help-section">
              <h4>Location methods:</h4>
              <ul>
                <li><strong>Postcode (Recommended):</strong> Most accurate - uses official UK postcode database for precise coordinates</li>
                <li><strong>Device Location:</strong> Quick option - uses your device's GPS (requires permission)</li>
                <li><strong>City Selection:</strong> Manual selection - choose from hierarchical list of UK locations</li>
              </ul>
            </div>

            <div className="help-section">
              <h4>Privacy & Data:</h4>
              <p>Your location data is only used for climate calculations and is not stored or shared. All weather data comes from public scientific databases (NASA POWER and Open-Meteo).</p>
            </div>
          </div>
        </div>
      </div>
    );
  };

  const renderMethodSelector = () => (
    <div className="method-selector">
      <div className="method-selector-header">
        <h2>Find Your Location</h2>
        <button 
          className="help-icon-btn"
          onClick={() => setShowHelp(true)}
          aria-label="Get help about location information"
          title="Get help about location information"
        >
          ?
        </button>
      </div>
      <p>Choose how you'd like to tell us your location to get personalized climate data.</p>

      <div className="method-tabs">
        <button
          className={`method-tab ${activeMethod === 'postcode' ? 'active' : ''}`}
          onClick={() => handleMethodChange('postcode')}
        >
          Postcode
        </button>
        <button
          className={`method-tab ${activeMethod === 'location' ? 'active' : ''}`}
          onClick={() => handleMethodChange('location')}
        >
          Current Location
        </button>
        <button
          className={`method-tab ${activeMethod === 'city' ? 'active' : ''}`}
          onClick={() => handleMethodChange('city')}
        >
          Select City
        </button>
      </div>
    </div>
  );

  const renderActiveMethod = () => {
    switch (activeMethod) {
      case 'postcode':
        return (
          <PostcodeInput
            onLocationSelect={handleLocationSelect}
            isLoading={isLoading}
            setIsLoading={setIsLoading}
          />
        );
      case 'location':
        return (
          <LocationInput
            onLocationSelect={handleLocationSelect}
            isLoading={isLoading}
            setIsLoading={setIsLoading}
          />
        );
      case 'city':
        return (
          <CitySelector
            onLocationSelect={handleLocationSelect}
            isLoading={isLoading}
            setIsLoading={setIsLoading}
          />
        );
      default:
        return null;
    }
  };

  const renderLocationSummary = () => {
    if (!locationData) return null;

    return (
      <div className="location-summary">
        <div className="summary-card">
          <h4>Location Found</h4>
          <div className="location-details">
            <div className="detail-row">
              <span>Location:</span>
              <strong>{locationData.location}</strong>
            </div>
            <div className="detail-row">
              <span>Postcode:</span>
              <strong>{locationData.postcode}</strong>
            </div>
            <div className="detail-row">
              <span>Coordinates:</span>
              <strong>{locationData.latitude.toFixed(4)}, {locationData.longitude.toFixed(4)}</strong>
            </div>
            <div className="detail-row">
              <span>Method:</span>
              <strong>
                {locationData.type === 'postcode' ? 'Postcode' : 
                 locationData.type === 'geolocation' ? 'Current Location' : 'City Selection'}
              </strong>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // 检查是否所有必填信息都已完成
  const isFormComplete = locationData && 
                         propertyInfo.propertyType && 
                         propertyInfo.buildingYear && 
                         propertyInfo.epcRating;

  return (
    <div className="step-container address-input-step">
      {renderMethodSelector()}

      <div className="method-content">
        {renderActiveMethod()}
      </div>

      {renderLocationSummary()}

      {locationData && (
        <ClimateChart
          latitude={locationData.latitude}
          longitude={locationData.longitude}
          location={locationData.location}
        />
      )}

      {renderPropertyInformation()}

      {renderFieldHelpModal()}

      <div className="button-group">
        <div></div>
        <button
          className={`btn btn-primary ${!isFormComplete ? 'disabled' : ''}`}
          onClick={handleNext}
          disabled={!isFormComplete || isLoading}
        >
          Next: House Details
        </button>
      </div>

      {renderHelpModal()}
    </div>
  );
};

export default BasicInformation;
