import React, { useState, useEffect } from "react";
import { tariffService } from "../../services/tariffService";

const Tariffs = ({ data, onDataChange, onNext, onBack }) => {
  const [formData, setFormData] = useState({
    electricityTariffType: data.electricityTariffType || 'standard',
    electricityRate: data.electricityRate || '',
    electricityStandingCharge: data.electricityStandingCharge || '',
    gasTariffType: data.gasTariffType || 'standard',
    gasRate: data.gasRate || '',
    gasStandingCharge: data.gasStandingCharge || '',
    feedInTariffRate: data.feedInTariffRate || '',
    smartExportGuarantee: data.smartExportGuarantee || '',
    timeOfUseEnabled: data.timeOfUseEnabled || false,
    peakRate: data.peakRate || '',
    offPeakRate: data.offPeakRate || '',
    nightRate: data.nightRate || '',
    economy7NightRate: data.economy7NightRate || '',
    economy10NightRate: data.economy10NightRate || '',
    annualPriceIncrease: data.annualPriceIncrease || '3',
    gasSupplier: data.gasSupplier || '',
    hasGasSupply: data.hasGasSupply !== false, // Default to true
    ...data
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const newValue = type === 'checkbox' ? checked : value;

    setFormData(prev => ({
      ...prev,
      [name]: newValue
    }));
    onDataChange({ [name]: newValue });
  };

  const handleNext = () => {
    // Basic validation - require main electricity rate
    if (formData.electricityRate) {
      onNext();
    }
  };

  const isFormValid = formData.electricityRate && parseFloat(formData.electricityRate) > 0;

  const majorSuppliers = [
    'British Gas', 'E.ON', 'EDF Energy', 'Npower', 'ScottishPower', 'SSE',
    'Bulb', 'Octopus Energy', 'Avro Energy', 'Together Energy', 'Green Supplier Limited',
    'Pure Planet', 'Shell Energy', 'So Energy', 'Utility Warehouse', 'Other'
  ];

  const getCurrentElectricityInfo = () => {
    if (!formData.electricityRate) return "Please enter your current electricity rate";

    const rate = parseFloat(formData.electricityRate);
    if (rate < 15) return "This rate seems low - please check it's in pence per kWh";
    if (rate > 50) return "This rate seems high - renewable energy could provide significant savings";
    if (rate >= 15 && rate <= 25) return "Standard rate - renewable energy will provide good savings";
    if (rate > 25 && rate <= 35) return "Above average rate - excellent potential for renewable energy savings";
    return "High rate - renewable energy systems will be very beneficial";
  };

  // Check if tariff type requires additional rate inputs
  const requiresTimeOfUseRates = () => {
    return formData.electricityTariffType === 'time-of-use';
  };

  const requiresEconomy7Rates = () => {
    return formData.electricityTariffType === 'economy7';
  };

  const requiresEconomy10Rates = () => {
    return formData.electricityTariffType === 'economy10';
  };

  return (
    <div className="step-container">
      <div className="step-header">
        <h2>Energy Tariffs</h2>
        <p>Configure your electricity and gas tariff details to calculate accurate savings and payback periods for your renewable energy system.</p>
      </div>

      {/* Electricity Tariff Section */}
      <div className="tariff-section">
        <h3>Electricity Tariff</h3>

        <div className="form-group">
          <label htmlFor="electricityTariffType">Tariff Type</label>
          <select
            id="electricityTariffType"
            name="electricityTariffType"
            value={formData.electricityTariffType}
            onChange={handleChange}
          >
            <option value="standard">Standard Variable Tariff</option>
            <option value="fixed">Fixed Rate Tariff</option>
            <option value="economy7">Economy 7</option>
            <option value="economy10">Economy 10</option>
            <option value="time-of-use">Time of Use/Agile Tariff</option>
          </select>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="electricityRate">
              {requiresEconomy7Rates() || requiresEconomy10Rates() ? 'Day Rate (pence per kWh) *' : 'Unit Rate (pence per kWh) *'}
            </label>
            <input
              type="number"
              id="electricityRate"
              name="electricityRate"
              value={formData.electricityRate}
              onChange={handleChange}
              placeholder="e.g. 28.5"
              step="0.1"
              min="0"
              required
            />
            <small>{getCurrentElectricityInfo()}</small>
          </div>

          <div className="form-group">
            <label htmlFor="electricityStandingCharge">Standing Charge (pence per day)</label>
            <input
              type="number"
              id="electricityStandingCharge"
              name="electricityStandingCharge"
              value={formData.electricityStandingCharge}
              onChange={handleChange}
              placeholder="e.g. 45.0"
              step="0.1"
              min="0"
            />
          </div>
        </div>

        {/* Economy 7 Rates */}
        {requiresEconomy7Rates() && (
          <div className="economy-rates-section">
            <h4>Economy 7 Rates</h4>
            <div className="form-group">
              <label htmlFor="economy7NightRate">Night Rate (pence per kWh)</label>
              <input
                type="number"
                id="economy7NightRate"
                name="economy7NightRate"
                value={formData.economy7NightRate}
                onChange={handleChange}
                placeholder="e.g. 12.0"
                step="0.1"
                min="0"
              />
              <small>Usually 12am-7am (7 hours)</small>
            </div>
          </div>
        )}

        {/* Economy 10 Rates */}
        {requiresEconomy10Rates() && (
          <div className="economy-rates-section">
            <h4>Economy 10 Rates</h4>
            <div className="form-group">
              <label htmlFor="economy10NightRate">Night Rate (pence per kWh)</label>
              <input
                type="number"
                id="economy10NightRate"
                name="economy10NightRate"
                value={formData.economy10NightRate}
                onChange={handleChange}
                placeholder="e.g. 10.0"
                step="0.1"
                min="0"
              />
              <small>Usually 12am-7am and 1pm-4pm (10 hours total)</small>
            </div>
          </div>
        )}

        {/* Time of Use Rates */}
        {requiresTimeOfUseRates() && (
          <div className="time-of-use-section">
            <h4>Time of Use/Agile Rates</h4>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="peakRate">Peak Rate (pence per kWh)</label>
                <input
                  type="number"
                  id="peakRate"
                  name="peakRate"
                  value={formData.peakRate}
                  onChange={handleChange}
                  placeholder="e.g. 35.0"
                  step="0.1"
                />
                <small>Usually 4pm-7pm weekdays</small>
              </div>

              <div className="form-group">
                <label htmlFor="offPeakRate">Off-Peak Rate (pence per kWh)</label>
                <input
                  type="number"
                  id="offPeakRate"
                  name="offPeakRate"
                  value={formData.offPeakRate}
                  onChange={handleChange}
                  placeholder="e.g. 15.0"
                  step="0.1"
                />
                <small>Usually 7pm-4pm</small>
              </div>

              <div className="form-group">
                <label htmlFor="nightRate">Standard Rate (pence per kWh)</label>
                <input
                  type="number"
                  id="nightRate"
                  name="nightRate"
                  value={formData.nightRate}
                  onChange={handleChange}
                  placeholder="e.g. 20.0"
                  step="0.1"
                />
                <small>Standard unit rate throughout the day</small>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Gas Tariff Section */}
      <div className="tariff-section">
        <h3>Gas Tariff</h3>

        <div className="form-group">
          <label className="checkbox-label">
            <input
              type="checkbox"
              name="hasGasSupply"
              checked={formData.hasGasSupply}
              onChange={handleChange}
            />
            <span className="checkmark"></span>
            I have a gas supply to my property
          </label>
        </div>

        {formData.hasGasSupply && (
          <>
            <div className="form-group">
              <label htmlFor="gasSupplier">Current Gas Supplier</label>
              <select
                id="gasSupplier"
                name="gasSupplier"
                value={formData.gasSupplier}
                onChange={handleChange}
              >
                <option value="">Select your supplier</option>
                {majorSuppliers.map(supplier => (
                  <option key={supplier} value={supplier}>{supplier}</option>
                ))}
              </select>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="gasRate">Unit Rate (pence per kWh)</label>
                <input
                  type="number"
                  id="gasRate"
                  name="gasRate"
                  value={formData.gasRate}
                  onChange={handleChange}
                  placeholder="e.g. 7.5"
                  step="0.1"
                  min="0"
                />
                <small>Gas rates are typically 4-10p per kWh</small>
              </div>

              <div className="form-group">
                <label htmlFor="gasStandingCharge">Standing Charge (pence per day)</label>
                                <input
                  type="number"
                  id="gasStandingCharge"
                  name="gasStandingCharge"
                  value={formData.gasStandingCharge}
                  onChange={handleChange}
                  placeholder="e.g. 27.0"
                  step="0.1"
                  min="0"
                />
              </div>
            </div>
          </>
        )}
      </div>

      {/* Export Tariff Section */}
      <div className="tariff-section">
        <h3>Export Tariffs</h3>
        <p>Rates you'll be paid for excess renewable energy exported to the grid</p>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="feedInTariffRate">Feed-in Tariff Rate (pence per kWh)</label>
            <input
              type="number"
              id="feedInTariffRate"
              name="feedInTariffRate"
              value={formData.feedInTariffRate}
              onChange={handleChange}
              placeholder="e.g. 5.5"
              step="0.1"
              min="0"
            />
            <small>Legacy Feed-in Tariff for existing installations</small>
          </div>

          <div className="form-group">
            <label htmlFor="smartExportGuarantee">Smart Export Guarantee (SEG) Rate (pence per kWh)</label>
            <input
              type="number"
              id="smartExportGuarantee"
              name="smartExportGuarantee"
              value={formData.smartExportGuarantee}
              onChange={handleChange}
              placeholder="e.g. 4.1"
              step="0.1"
              min="0"
            />
            <small>Current SEG rates typically 3-7p per kWh</small>
          </div>
        </div>
      </div>

      {/* Price Projections */}
      <div className="tariff-section">
        <h3>Price Projections</h3>

        <div className="form-group">
          <label htmlFor="annualPriceIncrease">Expected Annual Price Increase (%)</label>
          <select
            id="annualPriceIncrease"
            name="annualPriceIncrease"
            value={formData.annualPriceIncrease}
            onChange={handleChange}
          >
            <option value="0">0% - Prices remain stable</option>
            <option value="2">2% - Low inflation</option>
            <option value="3">3% - Moderate inflation</option>
            <option value="5">5% - Higher inflation</option>
            <option value="7">7% - High inflation</option>
            <option value="10">10% - Very high inflation</option>
          </select>
          <small>This affects long-term savings calculations. Recent trends show 3-7% annual increases.</small>
        </div>
      </div>

      {/* Tariff Summary */}
      {isFormValid && (
        <div className="tariff-summary">
          <h3>Current Tariff Summary</h3>
          <div className="summary-grid">
            <div className="summary-item">
              <span className="summary-label">Electricity Rate:</span>
              <span className="summary-value">{formData.electricityRate}p/kWh</span>
            </div>
            {formData.electricityStandingCharge && (
              <div className="summary-item">
                <span className="summary-label">Electricity Standing Charge:</span>
                <span className="summary-value">{formData.electricityStandingCharge}p/day</span>
              </div>
            )}
            {requiresEconomy7Rates() && formData.economy7NightRate && (
              <div className="summary-item">
                <span className="summary-label">Economy 7 Night Rate:</span>
                <span className="summary-value">{formData.economy7NightRate}p/kWh</span>
              </div>
            )}
            {requiresEconomy10Rates() && formData.economy10NightRate && (
              <div className="summary-item">
                <span className="summary-label">Economy 10 Night Rate:</span>
                <span className="summary-value">{formData.economy10NightRate}p/kWh</span>
              </div>
            )}
            {formData.hasGasSupply && formData.gasRate && (
              <div className="summary-item">
                <span className="summary-label">Gas Rate:</span>
                <span className="summary-value">{formData.gasRate}p/kWh</span>
              </div>
            )}
            {formData.smartExportGuarantee && (
              <div className="summary-item">
                <span className="summary-label">Export Rate (SEG):</span>
                <span className="summary-value">{formData.smartExportGuarantee}p/kWh</span>
              </div>
            )}
            <div className="summary-item">
              <span className="summary-label">Expected Annual Increase:</span>
              <span className="summary-value">{formData.annualPriceIncrease}%</span>
            </div>
          </div>
        </div>
      )}

      <div className="button-group">
        <button type="button" className="btn btn-secondary" onClick={onBack}>
          Back
        </button>
        <button 
          type="button" 
          className={`btn btn-primary ${!isFormValid ? 'disabled' : ''}`}
          onClick={handleNext}
          disabled={!isFormValid}
        >
          Calculate Results
        </button>
      </div>
    </div>
  );
};

export default Tariffs;
