import React, { useState, useEffect, useRef } from 'react';
import { Wrapper, Status } from '@googlemaps/react-wrapper';

const PostcodeLocationInput = ({ onLocationChange }) => {
  const [postcode, setPostcode] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!postcode.trim()) return;

    setIsLoading(true);
    setError('');

    try {
      // Use the same postcode service as in BasicInformation
      const response = await fetch(`https://api.postcodes.io/postcodes/${postcode.trim()}`);
      const data = await response.json();

      if (data.status === 200) {
        const location = {
          lat: data.result.latitude,
          lng: data.result.longitude,
          postcode: data.result.postcode,
          location: `${data.result.admin_ward}, ${data.result.admin_district}`,
          country: data.result.country
        };
        onLocationChange(location);
      } else {
        setError('Invalid postcode. Please check and try again.');
      }
    } catch (err) {
      setError('Unable to find location. Please check your postcode.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="postcode-location-input">
      <form onSubmit={handleSubmit} className="postcode-form">
        <div className="input-group">
          <input
            type="text"
            value={postcode}
            onChange={(e) => setPostcode(e.target.value.toUpperCase())}
            placeholder="Enter postcode (e.g. SW1A 1AA)"
            className="postcode-input"
            disabled={isLoading}
          />
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={isLoading || !postcode.trim()}
          >
            {isLoading ? 'Locating...' : 'Locate'}
          </button>
        </div>
        {error && <div className="error-message">{error}</div>}
      </form>
    </div>
  );
};

const GoogleMapsPolygonDrawer = ({ onAreaChange, userLocation, apiKey }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [polygon, setPolygon] = useState(null);
  const [area, setArea] = useState(0);
  const [currentLocation, setCurrentLocation] = useState(userLocation);

  // Update map center when location changes
  useEffect(() => {
    if (map && currentLocation) {
      map.setCenter(currentLocation);
      map.setZoom(20);
    }
  }, [currentLocation, map]);

  useEffect(() => {
    if (mapRef.current && !map) {
      const newMap = new window.google.maps.Map(mapRef.current, {
        center: currentLocation || { lat: 51.5074, lng: -0.1278 }, // London default
        zoom: 20,
        mapTypeId: 'satellite',
        tilt: 0,
      });

      // Enable drawing manager
      const drawingManager = new window.google.maps.drawing.DrawingManager({
        drawingMode: window.google.maps.drawing.OverlayType.POLYGON,
        drawingControl: true,
        drawingControlOptions: {
          position: window.google.maps.ControlPosition.TOP_CENTER,
          drawingModes: [window.google.maps.drawing.OverlayType.POLYGON],
        },
        polygonOptions: {
          fillColor: '#FF6B35',
          fillOpacity: 0.5,
          strokeColor: '#FF6B35',
          strokeWeight: 2,
          editable: true,
          draggable: true,
        },
      });

      drawingManager.setMap(newMap);

      // Handle polygon completion
      drawingManager.addListener('polygoncomplete', (newPolygon) => {
        // Remove previous polygon if exists
        if (polygon) {
          polygon.setMap(null);
        }

        setPolygon(newPolygon);
        calculateArea(newPolygon);

        // Add listener for polygon changes
        newPolygon.getPath().addListener('set_at', () => calculateArea(newPolygon));
        newPolygon.getPath().addListener('insert_at', () => calculateArea(newPolygon));
        newPolygon.getPath().addListener('remove_at', () => calculateArea(newPolygon));

        // Disable drawing after polygon is created
        drawingManager.setDrawingMode(null);
      });

      setMap(newMap);
    }
  }, [mapRef.current, map, currentLocation]);

  const calculateArea = (polygon) => {
    const path = polygon.getPath();
    const areaInSquareMeters = window.google.maps.geometry.spherical.computeArea(path);
    setArea(areaInSquareMeters);
    onAreaChange(areaInSquareMeters);
  };

  const clearPolygon = () => {
    if (polygon) {
      polygon.setMap(null);
      setPolygon(null);
      setArea(0);
      onAreaChange(0);
    }
  };

  const enableDrawing = () => {
    if (map) {
      const drawingManager = new window.google.maps.drawing.DrawingManager({
        drawingMode: window.google.maps.drawing.OverlayType.POLYGON,
        drawingControl: true,
        drawingControlOptions: {
          position: window.google.maps.ControlPosition.TOP_CENTER,
          drawingModes: [window.google.maps.drawing.OverlayType.POLYGON],
        },
        polygonOptions: {
          fillColor: '#FF6B35',
          fillOpacity: 0.5,
          strokeColor: '#FF6B35',
          strokeWeight: 2,
          editable: true,
          draggable: true,
        },
      });
      drawingManager.setMap(map);
    }
  };

  const handleLocationChange = (newLocation) => {
    setCurrentLocation(newLocation);
    // Clear existing polygon when location changes
    if (polygon) {
      clearPolygon();
    }
  };

  return (
    <div className="google-maps-container">
      <div className="map-location-controls">
        <h5>📍 Change Location</h5>
        <p>Enter a different postcode to locate another property:</p>
        <PostcodeLocationInput onLocationChange={handleLocationChange} />
      </div>

      <div ref={mapRef} style={{ height: '500px', width: '100%' }} />

      <div className="map-controls">
        <button onClick={clearPolygon} className="btn btn-secondary" disabled={!polygon}>
          Clear Roof Area
        </button>
        <button onClick={enableDrawing} className="btn btn-outline">
          Draw New Area
        </button>
        {area > 0 && (
          <div className="area-display">
            <strong>Roof Area: {area.toFixed(2)} m²</strong>
          </div>
        )}
      </div>
    </div>
  );
};

