import React from 'react';

const SolarPotentialCalculation = ({ 
  isReadyForCalculation, 
  onCalculate, 
  showResults, 
  result, 
  currentRoofNumber, 
  onAddRoof,
  onAddAnotherRoof
}) => {
  return (
    <>
      {/* Solar Potential Calculation Section */}
      {isReadyForCalculation && !showResults && (
        <div className="solar-potential-section">
          <button 
            type="button"
            className="btn btn-calculate"
            onClick={onCalculate}
          >
            Calculate Solar Potential
          </button>
        </div>
      )}

      {/* Solar Potential Results */}
      {showResults && result && (
        <div className="solar-potential-results">
          <div className="potential-result-header">
            <h4>Roof {currentRoofNumber}'s solar potential: {result.solarPotential.toFixed(2)}</h4>
            <div className="result-actions">
              <button 
                type="button"
                className="btn btn-add-roof"
                onClick={onAddRoof}
              >
                Add this roof
              </button>
              <button 
                type="button"
                className="btn btn-another-roof"
                onClick={onAddAnotherRoof}
              >
                Add another roof
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default SolarPotentialCalculation;
