import React, { useState } from "react";
import ConfigurationComparison from "./ConfigurationComparison";
import "../../styles/configuration-comparison.css";

const SystemConfiguration = ({ userData }) => {
  const [selectedEquipment, setSelectedEquipment] = useState({
    solarPanels: false,
    batteryStorage: false,
    heatPump: false
  });
  const [configurationConfirmed, setConfigurationConfirmed] = useState(false);
  const [showComparison, setShowComparison] = useState(false);

  const handleEquipmentChange = (equipment) => {
    setSelectedEquipment(prev => ({
      ...prev,
      [equipment]: !prev[equipment]
    }));
  };

  const handleConfirmConfiguration = () => {
    const hasSelectedEquipment = Object.values(selectedEquipment).some(selected => selected);
    if (hasSelectedEquipment) {
      setConfigurationConfirmed(true);
      setShowComparison(true);
    }
  };

  const handleBackToConfiguration = () => {
    setShowComparison(false);
    setConfigurationConfirmed(false);
  };

  const hasSelectedEquipment = Object.values(selectedEquipment).some(selected => selected);

  // If showing comparison, render the comparison component
  if (showComparison) {
    return (
      <ConfigurationComparison 
        selectedEquipment={selectedEquipment}
        userData={userData}
        onBack={handleBackToConfiguration}
      />
    );
  }

  return (
    <div className="system-configuration-tab">
      <div className="results-section">
        <div className="configuration-card">
          <h3>System Configuration Comparison</h3>
          <p className="configuration-intro">
            Which equipment would you like to install?
          </p>

          {!configurationConfirmed ? (
            /* Equipment Selection UI */
            <div className="equipment-selection">

              <div className="equipment-options">
                <div className="equipment-option">
                  <label className="equipment-checkbox">
                    <input
                      type="checkbox"
                      checked={selectedEquipment.solarPanels}
                      onChange={() => handleEquipmentChange('solarPanels')}
                    />
                    <span className="checkmark"></span>
                    <div className="equipment-info">
                      <span className="equipment-name">Solar Panels</span>
                    </div>
                  </label>
                </div>

                <div className="equipment-option">
                  <label className="equipment-checkbox">
                    <input
                      type="checkbox"
                      checked={selectedEquipment.batteryStorage}
                      onChange={() => handleEquipmentChange('batteryStorage')}
                    />
                    <span className="checkmark"></span>
                    <div className="equipment-info">
                      <span className="equipment-name">Battery Storage</span>
                    </div>
                  </label>
                </div>

                <div className="equipment-option">
                  <label className="equipment-checkbox">
                    <input
                      type="checkbox"
                      checked={selectedEquipment.heatPump}
                      onChange={() => handleEquipmentChange('heatPump')}
                    />
                    <span className="checkmark"></span>
                    <div className="equipment-info">
                      <span className="equipment-name">Heat Pump</span>
                    </div>
                  </label>
                </div>
              </div>

              {hasSelectedEquipment && (
                <div className="selection-summary">
                  <h5>Selected:</h5>
                  <div className="selected-tags">
                    {selectedEquipment.solarPanels && 
                      <span className="equipment-tag solar">Solar Panels</span>}
                    {selectedEquipment.batteryStorage && 
                      <span className="equipment-tag battery">Battery Storage</span>}
                    {selectedEquipment.heatPump && 
                      <span className="equipment-tag heatpump">Heat Pump</span>}
                  </div>
                </div>
              )}

              <div className="configuration-actions">
                <button 
                  className="btn btn-primary"
                  onClick={handleConfirmConfiguration}
                  disabled={!hasSelectedEquipment}
                >
                  Compare Equipment
                </button>
                {!hasSelectedEquipment && (
                  <p className="selection-hint">Select at least one equipment type</p>
                )}
              </div>
            </div>
          ) : (
            /* Configuration Results UI */
            <div className="configuration-results">
              <div className="configuration-confirmed">
                <h4>✓ Equipment Selected</h4>
                <div className="confirmed-equipment">
                  {selectedEquipment.solarPanels && 
                    <span className="equipment-tag solar">Solar Panels</span>}
                  {selectedEquipment.batteryStorage && 
                    <span className="equipment-tag battery">Battery Storage</span>}
                  {selectedEquipment.heatPump && 
                    <span className="equipment-tag heatpump">Heat Pump</span>}
                </div>

                <div className="comparison-actions">
                  <button 
                    className="btn btn-primary"
                    onClick={() => setShowComparison(true)}
                  >
                    Start Comparison
                  </button>
                  <button 
                    className="btn btn-secondary"
                    onClick={() => {
                      setConfigurationConfirmed(false);
                      setSelectedEquipment({ solarPanels: false, batteryStorage: false, heatPump: false });
                    }}
                  >
                    Change Selection
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SystemConfiguration;
