import React, { useState } from 'react';
import { Wrapper, Status } from '@googlemaps/react-wrapper';
import GoogleMapsPolygonDrawer from './roof-condition/GoogleMapsPolygonDrawer';
import RoofConfigurationForm from './roof-condition/RoofConfigurationForm';
import { calculateActualRoofArea } from './roof-condition/roofAreaUtils';

const render = (status) => {
  switch (status) {
    case Status.LOADING:
      return <div className="maps-loading">Loading Google Maps...</div>;
    case Status.FAILURE:
      return <div className="maps-error">Error loading Google Maps. Please check your internet connection.</div>;
    case Status.SUCCESS:
      return null;
  }
};

const RoofCondition = ({ data, onDataChange, userLocation }) => {
  const [projectedRoofArea, setProjectedRoofArea] = useState(data.roofArea || 0);
  const [roofConfig, setRoofConfig] = useState({
    roofOrientation: data.roofOrientation || '',
    roofAngle: data.roofAngle || '',
    roofShading: data.roofShading || '',
    roofUtilisation: data.roofUtilisation || ''
  });

  // Calculate actual roof area based on angle
  const actualRoofArea = calculateActualRoofArea(projectedRoofArea, roofConfig.roofAngle);

  const handleAreaChange = (areaFromMaps) => {
    const areaValue = areaFromMaps;
    setProjectedRoofArea(areaValue);

    // Calculate actual area and save both values
    const actualArea = calculateActualRoofArea(areaValue, roofConfig.roofAngle);
    onDataChange({ 
      roofArea: actualArea.toFixed(2),
      projectedRoofArea: areaValue.toFixed(2),
      useMapsForArea: true 
    });
  };

  const handleConfigChange = (field, value) => {
    const updatedConfig = {
      ...roofConfig,
      [field]: value
    };
    setRoofConfig(updatedConfig);

    // When roof angle changes, recalculate actual area
    if (field === 'roofAngle') {
      const actualArea = calculateActualRoofArea(projectedRoofArea, value);
      onDataChange({ 
        [field]: value,
        roofArea: actualArea.toFixed(2),
        projectedRoofArea: projectedRoofArea.toFixed(2)
      });
    } else {
      onDataChange({ [field]: value });
    }
  };

  const apiKey = "AIzaSyCpAABUQR_MiKb4yDovC3-1jM0LdIs59Hc";

  return (
    <div className="roof-condition-step">
      <div className="step-header">
        <h3>2.1 Roof Area Measurement</h3>
        <p>Use our interactive map to precisely measure your roof area for accurate solar panel calculations.</p>
      </div>

      <div className="roof-measurement-container">
        <div className="map-instructions">
          <h4>How to Measure Your Roof Area</h4>
          <div className="instructions-grid">
            <div className="instruction-step">
              <span className="step-number">1</span>
              <p>Use the polygon tool to trace your roof outline</p>
            </div>
            <div className="instruction-step">
              <span className="step-number">2</span>
              <p>Click to add points around your roof perimeter</p>
            </div>
            <div className="instruction-step">
              <span className="step-number">3</span>
              <p>Double-click to complete the polygon</p>
            </div>
            <div className="instruction-step">
              <span className="step-number">4</span>
              <p>Drag points to adjust the shape for accuracy</p>
            </div>
          </div>
          <div className="map-tips">
            <h5>Tips for Accurate Measurement:</h5>
            <ul>
              <li>Trace only the usable roof area (avoid chimneys, skylights, dormers)</li>
              <li>Include multiple roof sections if needed</li>
              <li>The satellite view helps identify the exact roof edges</li>
              <li>Be as precise as possible - this affects your solar panel calculations</li>
            </ul>
          </div>
        </div>

        <div className="form-group">
          <label>Interactive Roof Area Measurement</label>
          <Wrapper apiKey={apiKey} render={render} libraries={['drawing', 'geometry']}>
            <GoogleMapsPolygonDrawer 
              onAreaChange={handleAreaChange}
              userLocation={userLocation}
              apiKey={apiKey}
            />
          </Wrapper>
        </div>

        <RoofConfigurationForm
          roofConfig={roofConfig}
          onConfigChange={handleConfigChange}
          projectedArea={projectedRoofArea}
          visible={projectedRoofArea > 0}
        />
      </div>
    </div>
  );
};

export default RoofCondition;
