import React, { useState, useEffect } from 'react';
import { weatherService } from '../../../services/weatherService';

const ClimateChart = ({ latitude, longitude, location }) => {
  const [climateData, setClimateData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [tooltip, setTooltip] = useState({ show: false, x: 0, y: 0, content: '' });
  const [debugInfo, setDebugInfo] = useState('');

  useEffect(() => {
    if (latitude && longitude) {
      fetchClimateData();
    }
  }, [latitude, longitude]);

  const fetchClimateData = async () => {
    setIsLoading(true);
    setError('');
    setClimateData(null);
    setDebugInfo('');

    try {
      console.log('Fetching real climate data...');
      setDebugInfo(`Requesting data for: ${latitude}, ${longitude}`);

      const result = await weatherService.getCompleteClimateData(latitude, longitude);

      if (result.success) {
        setClimateData(result.data);
        setDebugInfo(`Successfully loaded data: ${result.data.solar.length} solar points, ${result.data.temperature.length} temperature points`);
        console.log('Climate data loaded successfully:', result.data);
      } else {
        setError(result.error || 'Failed to fetch climate data');
        setDebugInfo(`API Error: ${result.error}`);
        console.error('Climate data fetch failed:', result.error);
      }
    } catch (err) {
      const errorMsg = 'Failed to fetch climate data: ' + err.message;
      setError(errorMsg);
      setDebugInfo(`Exception: ${err.message}`);
      console.error('Climate data fetch error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const showTooltip = (e, month, solarValue, tempValue) => {
    const rect = e.currentTarget.getBoundingClientRect();
    setTooltip({
      show: true,
      x: e.clientX - rect.left,
      y: e.clientY - rect.top,
      content: {
        month,
        solar: solarValue.toFixed(1),
        temperature: tempValue.toFixed(1)
      }
    });
  };

  const hideTooltip = () => {
    setTooltip({ show: false, x: 0, y: 0, content: '' });
  };

  const renderCombinedChart = () => {
    if (!climateData?.solar || !climateData?.temperature) return null;

    const chartWidth = 500;
    const chartHeight = 300;
    const padding = 60;
    const months = climateData.solar;

    // 验证数据完整性
    if (months.length === 0 || climateData.temperature.length === 0) {
      return (
        <div className="chart-error">
          <p>Insufficient data to display chart</p>
          <small>Solar: {months.length} points, Temperature: {climateData.temperature.length} points</small>
        </div>
      );
    }

    // 计算太阳辐射的范围和比例
    const solarValues = months.map(d => d.value).filter(v => !isNaN(v) && v > 0);
    if (solarValues.length === 0) {
      return (
        <div className="chart-error">
          <p>No valid solar radiation data available</p>
          <small>Raw data points: {months.length}</small>
        </div>
      );
    }

    const solarMax = Math.max(...solarValues);
    const solarMin = Math.min(...solarValues);
    const solarRange = solarMax - solarMin || 1; // 避免除零

    // 计算温度的范围和比例
    const tempValues = climateData.temperature.map(d => d.value).filter(v => !isNaN(v));
    if (tempValues.length === 0) {
      return (
        <div className="chart-error">
          <p>No valid temperature data available</p>
          <small>Raw data points: {climateData.temperature.length}</small>
        </div>
      );
    }

    const tempMax = Math.max(...tempValues);
    const tempMin = Math.min(...tempValues);
    const tempRange = tempMax - tempMin || 1; // 避免除零

    // 生成太阳辐射折线点
    const solarPoints = months.map((item, index) => {
      const x = padding + (index * (chartWidth - 2 * padding)) / (months.length - 1);
      const y = chartHeight - padding - ((item.value - solarMin) / solarRange) * (chartHeight - 2 * padding);
      return { x, y, value: item.value, month: item.month };
    });

    // 生成温度折线点
    const tempPoints = climateData.temperature.map((item, index) => {
      const x = padding + (index * (chartWidth - 2 * padding)) / (months.length - 1);
      const y = chartHeight - padding - ((item.value - tempMin) / tempRange) * (chartHeight - 2 * padding);
      return { x, y, value: item.value, month: item.month };
    });

    const solarLine = solarPoints.map(p => `${p.x},${p.y}`).join(' ');
    const tempLine = tempPoints.map(p => `${p.x},${p.y}`).join(' ');

    return (
      <div className="combined-chart-container">
        <h4>Monthly Climate Data</h4>

        {/* 数据来源说明 */}
        <div className="data-source">
          <small>Data sources: NASA POWER (Solar), Open-Meteo (Temperature)</small>
        </div>

        {/* 图例 */}
        <div className="chart-legend">
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#f39c12' }}></div>
            <span>Solar Radiation (kWh/m²/day)</span>
          </div>
          <div className="legend-item">
            <div className="legend-color" style={{ backgroundColor: '#e74c3c' }}></div>
            <span>Temperature (°C)</span>
          </div>
        </div>

        <div className="chart-wrapper" style={{ position: 'relative' }}>
          <svg width={chartWidth} height={chartHeight} className="climate-chart">
            {/* 网格线 */}
            {[0, 0.25, 0.5, 0.75, 1].map(ratio => (
              <line
                key={ratio}
                x1={padding}
                y1={chartHeight - padding - ratio * (chartHeight - 2 * padding)}
                x2={chartWidth - padding}
                y2={chartHeight - padding - ratio * (chartHeight - 2 * padding)}
                stroke="#e0e0e0"
                strokeWidth="1"
              />
            ))}

            {/* 太阳辐射折线 */}
            <polyline
              points={solarLine}
              fill="none"
              stroke="#f39c12"
              strokeWidth="3"
              strokeLinecap="round"
              strokeLinejoin="round"
            />

            {/* 温度折线 */}
            <polyline
              points={tempLine}
              fill="none"
              stroke="#e74c3c"
              strokeWidth="3"
              strokeLinecap="round"
              strokeLinejoin="round"
            />

            {/* 太阳辐射数据点 */}
            {solarPoints.map((point, index) => (
              <circle
                key={`solar-${index}`}
                cx={point.x}
                cy={point.y}
                r="5"
                fill="#f39c12"
                stroke="white"
                strokeWidth="2"
                className="data-point solar-point"
                onMouseEnter={(e) => showTooltip(e, point.month, point.value, tempPoints[index].value)}
                onMouseLeave={hideTooltip}
                style={{ cursor: 'pointer' }}
              />
            ))}

            {/* 温度数据点 */}
            {tempPoints.map((point, index) => (
              <circle
                key={`temp-${index}`}
                cx={point.x}
                cy={point.y}
                r="5"
                fill="#e74c3c"
                stroke="white"
                strokeWidth="2"
                className="data-point temp-point"
                onMouseEnter={(e) => showTooltip(e, point.month, solarPoints[index].value, point.value)}
                onMouseLeave={hideTooltip}
                style={{ cursor: 'pointer' }}
              />
            ))}

            {/* X轴标签 */}
            {months.map((item, index) => {
              const x = padding + (index * (chartWidth - 2 * padding)) / (months.length - 1);
              return (
                <text
                  key={index}
                  x={x}
                  y={chartHeight - 15}
                  textAnchor="middle"
                  fontSize="12"
                  fill="#666"
                >
                  {item.month}
                </text>
              );
            })}

            {/* 左Y轴标签（太阳辐射） */}
            {[0, 0.5, 1].map((ratio, index) => {
              const value = solarMin + ratio * solarRange;
              const y = chartHeight - padding - ratio * (chartHeight - 2 * padding);
              return (
                <text
                  key={`solar-y-${index}`}
                  x={padding - 15}
                  y={y + 4}
                  textAnchor="end"
                  fontSize="11"
                  fill="#f39c12"
                  fontWeight="500"
                >
                  {value.toFixed(1)}
                </text>
              );
            })}

            {/* 右Y轴标签（温度） */}
            {[0, 0.5, 1].map((ratio, index) => {
              const value = tempMin + ratio * tempRange;
              const y = chartHeight - padding - ratio * (chartHeight - 2 * padding);
              return (
                <text
                  key={`temp-y-${index}`}
                  x={chartWidth - padding + 15}
                  y={y + 4}
                  textAnchor="start"
                  fontSize="11"
                  fill="#e74c3c"
                  fontWeight="500"
                >
                  {value.toFixed(1)}°C
                </text>
              );
            })}

            {/* Y轴标题 */}
            <text
              x={20}
              y={chartHeight / 2}
              textAnchor="middle"
              fontSize="12"
              fill="#f39c12"
              fontWeight="600"
              transform={`rotate(-90 20 ${chartHeight / 2})`}
            >
              Solar Radiation (kWh/m²/day)
            </text>

            <text
              x={chartWidth - 20}
              y={chartHeight / 2}
              textAnchor="middle"
              fontSize="12"
              fill="#e74c3c"
              fontWeight="600"
              transform={`rotate(90 ${chartWidth - 20} ${chartHeight / 2})`}
            >
              Temperature (°C)
            </text>
          </svg>

          {/* Tooltip */}
          {tooltip.show && (
            <div
              className="chart-tooltip"
              style={{
                position: 'absolute',
                left: tooltip.x + 10,
                top: tooltip.y - 10,
                transform: 'translateY(-100%)',
                pointerEvents: 'none'
              }}
            >
              <div className="tooltip-content">
                <div className="tooltip-title">{tooltip.content.month}</div>
                <div className="tooltip-item solar">
                  <span className="tooltip-color" style={{ backgroundColor: '#f39c12' }}></span>
                  Solar: {tooltip.content.solar} kWh/m²/day
                </div>
                <div className="tooltip-item temp">
                  <span className="tooltip-color" style={{ backgroundColor: '#e74c3c' }}></span>
                  Temperature: {tooltip.content.temperature}°C
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="chart-stats-combined">
          <div className="stat-group">
            <h5>Solar Radiation</h5>
            <div className="stat">
              <span>Annual Average:</span>
              <strong>{(solarValues.reduce((sum, val) => sum + val, 0) / solarValues.length).toFixed(1)} kWh/m²/day</strong>
            </div>
            <div className="stat">
              <span>Peak Month:</span>
              <strong>{months[solarValues.indexOf(Math.max(...solarValues))].month}</strong>
            </div>
            <div className="stat">
              <span>Data Points:</span>
              <strong>{solarValues.length} months</strong>
            </div>
          </div>

          <div className="stat-group">
            <h5>Temperature</h5>
            <div className="stat">
              <span>Annual Average:</span>
              <strong>{(tempValues.reduce((sum, val) => sum + val, 0) / tempValues.length).toFixed(1)}°C</strong>
            </div>
            <div className="stat">
              <span>Peak Month:</span>
              <strong>{climateData.temperature[tempValues.indexOf(Math.max(...tempValues))].month}</strong>
            </div>
            <div className="stat">
              <span>Data Points:</span>
              <strong>{tempValues.length} months</strong>
            </div>
          </div>
        </div>
      </div>
    );
  };

  if (!latitude || !longitude) {
    return (
      <div className="climate-chart-placeholder">
        <p>Select a location to view climate data</p>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="climate-chart-loading">
        <div className="loading-spinner">Loading</div>
        <p>Fetching real climate data from NASA POWER and Open-Meteo...</p>
        <small>This may take a few seconds</small>
        {debugInfo && <div style={{ marginTop: '0.5rem', fontSize: '0.8rem', color: '#666' }}>{debugInfo}</div>}
      </div>
    );
  }

  if (error) {
    return (
      <div className="climate-chart-error">
        <p>Warning: {error}</p>
        <button onClick={fetchClimateData} className="btn btn-secondary">
          Try Again
        </button>
        <div style={{ marginTop: '1rem' }}>
          <small>
            <strong>Troubleshooting:</strong><br/>
            • Check your internet connection<br/>
            • The APIs may be temporarily unavailable<br/>
            • Some locations may not have complete data coverage<br/>
            • Location: {latitude}, {longitude}
          </small>
          {debugInfo && (
            <div style={{ marginTop: '0.5rem', fontSize: '0.8rem', color: '#666', fontFamily: 'monospace' }}>
              Debug: {debugInfo}
            </div>
          )}
        </div>
      </div>
    );
  }

  // 计算数据年份范围用于显示
  const currentYear = new Date().getFullYear();
  const endYear = currentYear - 1;
  const startYear = endYear - 4; // 5年数据

  return (
    <div className="climate-chart-container">
      <div className="chart-header">
        <h3>Climate Data for {location}</h3>
        <p>Historical data ({startYear}-{endYear}) from NASA POWER and Open-Meteo APIs</p>
      </div>

      <div className="chart-content">
        {renderCombinedChart()}
      </div>
    </div>
  );
};

export default ClimateChart;
