import axios from "axios";

const API_BASE = "http://localhost:8080/api";

export const getComponents = (type) => axios.get(`${API_BASE}/${type}`);
export const calculate = (data) => axios.post(`${API_BASE}/calculate`, data);
export const calculateSolarPotential = (data) => axios.post(`${API_BASE}/solar-potential`, data);

// New Location APIs
export const getAllRegions = () => axios.get(`${API_BASE}/locations/regions`);

export const getRegionsByCountry = (country) => axios.get(`${API_BASE}/locations/regions/country/${country}`);

export const searchCitiesByName = (name) => axios.get(`${API_BASE}/locations/cities/search?name=${name}`);

export const searchCitiesByPostcode = (prefix) => axios.get(`${API_BASE}/locations/cities/postcode?prefix=${prefix}`);

export const getCitiesByCountry = (country) => axios.get(`${API_BASE}/locations/cities/country/${country}`);
