import React, { useState } from "react";

const ExistingSystems = ({ data, onDataChange, onNext, onBack }) => {
  const [formData, setFormData] = useState({
    heatingSystem: data.heatingSystem || "",
    heatingAge: data.heatingAge || "",
    hotWaterSystem: data.hotWaterSystem || "",
    monthlyElectricityBill: data.monthlyElectricityBill || "",
    monthlyGasBill: data.monthlyGasBill || "",
    existingRenewables: data.existingRenewables || [],
    renewableDevices: data.renewableDevices || {},
    energyRating: data.energyRating || "",
    ...data
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    onDataChange({ [name]: value });
  };

  const handleRenewableChange = (renewableType) => {
    const currentRenewables = Array.isArray(formData.existingRenewables) 
      ? formData.existingRenewables 
      : [];

    let updatedRenewables;
    let updatedDevices = { ...formData.renewableDevices };

    if (currentRenewables.includes(renewableType)) {
      // Remove the renewed energy type
      updatedRenewables = currentRenewables.filter(type => type !== renewableType);
      // Remove device info when unchecking
      delete updatedDevices[renewableType];
    } else {
      // Add the renewable energy type
      updatedRenewables = [...currentRenewables, renewableType];
    }

    const newData = {
      existingRenewables: updatedRenewables,
      renewableDevices: updatedDevices
    };

    setFormData(prev => ({
      ...prev,
      ...newData
    }));
    onDataChange(newData);
  };

  const handleDeviceInfoChange = (renewableType, field, value) => {
    const updatedDevices = {
      ...formData.renewableDevices,
      [renewableType]: {
        ...formData.renewableDevices[renewableType],
        [field]: value
      }
    };

    setFormData(prev => ({
      ...prev,
      renewableDevices: updatedDevices
    }));
    onDataChange({ renewableDevices: updatedDevices });
  };

  const handleNext = () => {
    if (formData.heatingSystem && formData.hotWaterSystem && formData.monthlyElectricityBill) {
      onNext();
    }
  };

  const isFormValid = formData.heatingSystem && formData.hotWaterSystem && formData.monthlyElectricityBill;

  const renewableOptions = [
    { value: 'solar-panels', label: 'Solar Panels' },
    { value: 'heat-pump', label: 'Heat Pump' },
    { value: 'battery', label: 'Battery Storage' }
  ];

  return (
    <div className="step-container">
      <div className="step-header">
        <h2>Energy Demand</h2>
        <p>Tell us about your current heating and energy systems to calculate potential replacements and savings.</p>
      </div>

      <div className="form-group">
        <label htmlFor="heatingSystem">Primary Heating System *</label>
        <select
          id="heatingSystem"
          name="heatingSystem"
          value={formData.heatingSystem}
          onChange={handleChange}
          required
        >
          <option value="">Select heating system</option>
          <option value="modern-gas">Modern gas system (installed after 2005)</option>
          <option value="older-gas">Older gas system (installed before 2005)</option>
          <option value="modern-oil">Modern oil system (installed after 2005)</option>
          <option value="older-oil">Older oil system (installed before 2005)</option>
          <option value="modern-lpg">Modern LPG system (installed after 2005)</option>
          <option value="older-lpg">Older LPG system (installed before 2005)</option>
          <option value="storage-heaters">Storage heaters</option>
          <option value="coal-heating">Coal heating system</option>
          <option value="biomass-heating">Biomass heating system</option>
          <option value="other-electric">Other electric system</option>
          <option value="existing-heat-pump">Existing heat pump</option>
        </select>
      </div>

      <div className="form-group">
        <label htmlFor="heatingAge">Age of Heating System</label>
        <select
          id="heatingAge"
          name="heatingAge"
          value={formData.heatingAge}
          onChange={handleChange}
        >
          <option value="">Select age</option>
          <option value="0-5">0-5 years (Very new)</option>
          <option value="5-10">5-10 years (Recent)</option>
          <option value="10-15">10-15 years (Moderate age)</option>
          <option value="15-20">15-20 years (Aging)</option>
          <option value="20+">20+ years (Old)</option>
          <option value="unknown">Don't know</option>
        </select>
        <small>Older systems are typically less efficient and good candidates for replacement</small>
      </div>

      <div className="form-group">
        <label htmlFor="hotWaterSystem">Hot Water System *</label>
        <select
          id="hotWaterSystem"
          name="hotWaterSystem"
          value={formData.hotWaterSystem}
          onChange={handleChange}
          required
        >
          <option value="">Select hot water system</option>
          <option value="combi-boiler">Combi Boiler</option>
          <option value="hot-water-tank">Hot Water Tank</option>
          <option value="electric-immersion">Electric Immersion</option>
          <option value="instant-electric">Instant Electric</option>
        </select>
      </div>

      <div className="form-group">
        <label htmlFor="monthlyElectricityBill">Average Monthly Electricity Bill *</label>
        <select
          id="monthlyElectricityBill"
          name="monthlyElectricityBill"
          value={formData.monthlyElectricityBill}
          onChange={handleChange}
          required
        >
          <option value="">Select range</option>
          <option value="0-50">£0-50</option>
          <option value="50-100">£50-100</option>
          <option value="100-150">£100-150</option>
          <option value="150-200">£150-200</option>
          <option value="200-300">£200-300</option>
          <option value="300+">£300+</option>
        </select>
        <small>This helps us calculate potential savings from renewable energy</small>
      </div>

      <div className="form-group">
        <label htmlFor="monthlyGasBill">Average Monthly Gas Bill</label>
        <select
          id="monthlyGasBill"
          name="monthlyGasBill"
          value={formData.monthlyGasBill}
          onChange={handleChange}
        >
          <option value="">Select range (leave blank if no gas)</option>
          <option value="0-50">£0-50</option>
          <option value="50-100">£50-100</option>
          <option value="100-150">£100-150</option>
          <option value="150-200">£150-200</option>
          <option value="200-300">£200-300</option>
          <option value="300+">£300+</option>
          <option value="no-gas">No gas supply</option>
        </select>
        <small>Gas costs help us calculate heat pump replacement benefits</small>
      </div>

      <div className="form-group">
        <label>Existing Renewable Energy Systems</label>
        <small>Select all renewable energy systems you currently have installed</small>
        <div className="checkbox-group">
          {renewableOptions.map((option) => (
            <div key={option.value} className="checkbox-item">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={Array.isArray(formData.existingRenewables) && formData.existingRenewables.includes(option.value)}
                  onChange={() => handleRenewableChange(option.value)}
                />
                <span className="checkmark"></span>
                {option.label}
              </label>

              {/* Show device info inputs when renewable type is selected */}
              {Array.isArray(formData.existingRenewables) && formData.existingRenewables.includes(option.value) && (
                <div className="device-info">
                  <div className="device-input-group">
                    <input
                      type="text"
                      placeholder="Brand/Manufacturer"
                      value={formData.renewableDevices[option.value]?.brand || ''}
                      onChange={(e) => handleDeviceInfoChange(option.value, 'brand', e.target.value)}
                      className="device-input"
                    />
                    <input
                      type="text"
                      placeholder="Model Number"
                      value={formData.renewableDevices[option.value]?.model || ''}
                      onChange={(e) => handleDeviceInfoChange(option.value, 'model', e.target.value)}
                      className="device-input"
                    />
                  </div>
                  <div className="device-input-group">
                    <input
                      type="text"
                      placeholder="Installation Year"
                      value={formData.renewableDevices[option.value]?.year || ''}
                      onChange={(e) => handleDeviceInfoChange(option.value, 'year', e.target.value)}
                      className="device-input small"
                    />
                    <input
                      type="text"
                      placeholder="Capacity/Size"
                      value={formData.renewableDevices[option.value]?.capacity || ''}
                      onChange={(e) => handleDeviceInfoChange(option.value, 'capacity', e.target.value)}
                      className="device-input"
                    />
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>

        {/* Option for no renewable systems */}
        <div className="checkbox-item no-renewables">
          <label className="checkbox-label">
            <input
              type="checkbox"
              checked={Array.isArray(formData.existingRenewables) && formData.existingRenewables.length === 0}
              onChange={() => {
                setFormData(prev => ({
                  ...prev,
                  existingRenewables: [],
                  renewableDevices: {}
                }));
                onDataChange({ existingRenewables: [], renewableDevices: {} });
              }}
            />
            <span className="checkmark"></span>
            No existing renewable energy systems
          </label>
        </div>
      </div>

      <div className="form-group">
        <label htmlFor="energyRating">Current Energy Performance Certificate (EPC) Rating</label>
        <select
          id="energyRating"
          name="energyRating"
          value={formData.energyRating}
          onChange={handleChange}
        >
          <option value="">Select EPC rating (if known)</option>
          <option value="A">A (Very efficient)</option>
          <option value="B">B (Efficient)</option>
          <option value="C">C (Fairly efficient)</option>
          <option value="D">D (Average)</option>
          <option value="E">E (Inefficient)</option>
          <option value="F">F (Very inefficient)</option>
          <option value="G">G (Extremely inefficient)</option>
          <option value="unknown">Don't know</option>
        </select>
        <small>EPC ratings help us understand your home's current energy efficiency</small>
      </div>

      <div className="button-group">
        <button 
          type="button" 
          className="btn btn-secondary"
          onClick={onBack}
        >
          Back
        </button>
        <button 
          type="button" 
          className={`btn btn-primary ${!isFormValid ? 'disabled' : ''}`}
          onClick={handleNext}
          disabled={!isFormValid}
        >
          Next: Solar Configuration
        </button>
      </div>
    </div>
  );
};

export default ExistingSystems;
