import React from 'react';
import { getOrientationLabel, getShadingLabel, getUtilisationLabel } from './roofLabelUtils';

const RoofSelectionList = ({ addedRoofs, onRoofsUpdate }) => {
  if (addedRoofs.length === 0) return null;

  const handleRoofSelectionChange = (roofId, selected) => {
    const updatedRoofs = addedRoofs.map(roof => 
      roof.id === roofId ? { ...roof, selected } : roof
    );
    onRoofsUpdate(updatedRoofs);
  };

  return (
    <div className="roof-selection-section">
      <h4> Select Roofs for Solar Installation</h4>
      <p className="selection-instruction">
        Choose which roofs you want to install solar panels on. You can select multiple roofs or change your selection anytime.
      </p>

      <div className="roofs-selection-grid">
        {addedRoofs.map((roof) => (
          <div key={roof.id} className={`roof-selection-card ${roof.selected ? 'selected' : ''}`}>
            <div className="roof-selection-header">
              <div className="roof-info">
                <h5>Roof {roof.id}</h5>
                <div className="roof-potential">
                  <span className="potential-value">{roof.solarPotential.toFixed(2)}</span>
                  <span className="potential-label">Solar Potential</span>
                </div>
              </div>
              <label className="selection-checkbox">
                <input
                  type="checkbox"
                  checked={roof.selected}
                  onChange={(e) => handleRoofSelectionChange(roof.id, e.target.checked)}
                />
                <span className="checkmark"></span>
              </label>
            </div>

            <div className="roof-selection-details">
              <div className="roof-detail">
                <span className="detail-label">Area:</span>
                <span className="detail-value">{roof.area.toFixed(2)} m²</span>
              </div>
              <div className="roof-detail">
                <span className="detail-label">Orientation:</span>
                <span className="detail-value">{getOrientationLabel(roof.orientation)}</span>
              </div>
              <div className="roof-detail">
                <span className="detail-label">Angle:</span>
                <span className="detail-value">{roof.angle}°</span>
              </div>
              <div className="roof-detail">
                <span className="detail-label">Shading:</span>
                <span className="detail-value">{getShadingLabel(roof.shading)}</span>
              </div>
              <div className="roof-detail">
                <span className="detail-label">Utilisation:</span>
                <span className="detail-value">{getUtilisationLabel(roof.utilisation)}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RoofSelectionList;
