import React, { useState, useEffect, useRef } from 'react';
import { Wrapper, Status } from '@googlemaps/react-wrapper';

const PostcodeLocationInput = ({ onLocationChange }) => {
  const [postcode, setPostcode] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!postcode.trim()) return;

    setIsLoading(true);
    setError('');

    try {
      // Use the same postcode service as in BasicInformation
      const response = await fetch(`https://api.postcodes.io/postcodes/${postcode.trim()}`);
      const data = await response.json();

      if (data.status === 200) {
        const location = {
          lat: data.result.latitude,
          lng: data.result.longitude,
          postcode: data.result.postcode,
          location: `${data.result.admin_ward}, ${data.result.admin_district}`,
          country: data.result.country
        };
        onLocationChange(location);
      } else {
        setError('Invalid postcode. Please check and try again.');
      }
    } catch (err) {
      setError('Unable to find location. Please check your postcode.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="postcode-location-input">
      <form onSubmit={handleSubmit} className="postcode-form">
        <div className="input-group">
          <input
            type="text"
            value={postcode}
            onChange={(e) => setPostcode(e.target.value.toUpperCase())}
            placeholder="Enter postcode (e.g. SW1A 1AA)"
            className="postcode-input"
            disabled={isLoading}
          />
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={isLoading || !postcode.trim()}
          >
            {isLoading ? 'Locating...' : 'Locate'}
          </button>
        </div>
        {error && <div className="error-message">{error}</div>}
      </form>
    </div>
  );
};

const GoogleMapsPolygonDrawer = ({ onAreaChange, userLocation, apiKey }) => {
  const mapRef = useRef(null);
  const [map, setMap] = useState(null);
  const [polygon, setPolygon] = useState(null);
  const [area, setArea] = useState(0);
  const [currentLocation, setCurrentLocation] = useState(userLocation);

  // Update map center when location changes
  useEffect(() => {
    if (map && currentLocation) {
      map.setCenter(currentLocation);
      map.setZoom(20);
    }
  }, [currentLocation, map]);

  useEffect(() => {
    if (mapRef.current && !map) {
      const newMap = new window.google.maps.Map(mapRef.current, {
        center: currentLocation || { lat: 51.5074, lng: -0.1278 }, // London default
        zoom: 20,
        mapTypeId: 'satellite',
        tilt: 0,
      });

      // Enable drawing manager
      const drawingManager = new window.google.maps.drawing.DrawingManager({
        drawingMode: window.google.maps.drawing.OverlayType.POLYGON,
        drawingControl: true,
        drawingControlOptions: {
          position: window.google.maps.ControlPosition.TOP_CENTER,
          drawingModes: [window.google.maps.drawing.OverlayType.POLYGON],
        },
        polygonOptions: {
          fillColor: '#FF6B35',
          fillOpacity: 0.5,
          strokeColor: '#FF6B35',
          strokeWeight: 2,
          editable: true,
          draggable: true,
        },
      });

      drawingManager.setMap(newMap);

      // Handle polygon completion
      drawingManager.addListener('polygoncomplete', (newPolygon) => {
        // Remove previous polygon if exists
        if (polygon) {
          polygon.setMap(null);
        }

        setPolygon(newPolygon);
        calculateArea(newPolygon);

        // Add listener for polygon changes
        newPolygon.getPath().addListener('set_at', () => calculateArea(newPolygon));
        newPolygon.getPath().addListener('insert_at', () => calculateArea(newPolygon));
        newPolygon.getPath().addListener('remove_at', () => calculateArea(newPolygon));

        // Disable drawing after polygon is created
        drawingManager.setDrawingMode(null);
      });

      setMap(newMap);
    }
  }, [mapRef.current, map, currentLocation]);

  const calculateArea = (polygon) => {
    const path = polygon.getPath();
    const areaInSquareMeters = window.google.maps.geometry.spherical.computeArea(path);
    setArea(areaInSquareMeters);
    onAreaChange(areaInSquareMeters);
  };

  const clearPolygon = () => {
    if (polygon) {
      polygon.setMap(null);
      setPolygon(null);
      setArea(0);
      onAreaChange(0);
    }
  };

  const enableDrawing = () => {
    if (map) {
      const drawingManager = new window.google.maps.drawing.DrawingManager({
        drawingMode: window.google.maps.drawing.OverlayType.POLYGON,
        drawingControl: true,
        drawingControlOptions: {
          position: window.google.maps.ControlPosition.TOP_CENTER,
          drawingModes: [window.google.maps.drawing.OverlayType.POLYGON],
        },
        polygonOptions: {
          fillColor: '#FF6B35',
          fillOpacity: 0.5,
          strokeColor: '#FF6B35',
          strokeWeight: 2,
          editable: true,
          draggable: true,
        },
      });
      drawingManager.setMap(map);
    }
  };

  const handleLocationChange = (newLocation) => {
    setCurrentLocation(newLocation);
    // Clear existing polygon when location changes
    if (polygon) {
      clearPolygon();
    }
  };

  return (
    <div className="google-maps-container">
      <div className="map-location-controls">
        <h5>Change Location</h5>
        <p>Enter a different postcode to locate another property:</p>
        <PostcodeLocationInput onLocationChange={handleLocationChange} />
      </div>

      <div ref={mapRef} style={{ height: '500px', width: '100%' }} />

      <div className="map-controls">
        <button onClick={clearPolygon} className="btn btn-secondary" disabled={!polygon}>
          Clear Roof Area
        </button>
        <button onClick={enableDrawing} className="btn btn-outline">
          Draw New Area
        </button>
        {area > 0 && (
          <div className="area-display">
            <strong>Projected Area: {area.toFixed(2)} m²</strong>
          </div>
        )}
      </div>
    </div>
  );
};

const render = (status) => {
  switch (status) {
    case Status.LOADING:
      return <div className="maps-loading">Loading Google Maps...</div>;
    case Status.FAILURE:
      return <div className="maps-error">Error loading Google Maps. Please check your internet connection.</div>;
    case Status.SUCCESS:
      return null;
  }
};

const RoofCondition = ({ data, onDataChange, userLocation }) => {
  const [projectedRoofArea, setProjectedRoofArea] = useState(data.roofArea || 0);
  const [roofConfig, setRoofConfig] = useState({
    roofOrientation: data.roofOrientation || '',
    roofAngle: data.roofAngle || '',
    roofShading: data.roofShading || '',
    roofUtilisation: data.roofUtilisation || ''
  });

  // Calculate actual roof area based on angle
  const calculateActualRoofArea = (projectedArea, angle) => {
    if (!projectedArea || angle === '' || angle === null) return projectedArea;

    const angleInRadians = (parseFloat(angle) * Math.PI) / 180;
    const cosAngle = Math.cos(angleInRadians);

    // Avoid division by zero for 90-degree angles
    if (Math.abs(cosAngle) < 0.001) return projectedArea * 100; // Very steep roof

    return projectedArea / cosAngle;
  };

  const actualRoofArea = calculateActualRoofArea(projectedRoofArea, roofConfig.roofAngle);

  const handleAreaChange = (areaFromMaps) => {
    const areaValue = areaFromMaps;
    setProjectedRoofArea(areaValue);

    // Calculate actual area and save both values
    const actualArea = calculateActualRoofArea(areaValue, roofConfig.roofAngle);
    onDataChange({ 
      roofArea: actualArea.toFixed(2),
      projectedRoofArea: areaValue.toFixed(2),
      useMapsForArea: true 
    });
  };

  const handleConfigChange = (field, value) => {
    const updatedConfig = {
      ...roofConfig,
      [field]: value
    };
    setRoofConfig(updatedConfig);

    // When roof angle changes, recalculate actual area
    if (field === 'roofAngle') {
      const actualArea = calculateActualRoofArea(projectedRoofArea, value);
      onDataChange({ 
        [field]: value,
        roofArea: actualArea.toFixed(2),
        projectedRoofArea: projectedRoofArea.toFixed(2)
      });
    } else {
      onDataChange({ [field]: value });
    }
  };

  // Generate roof angle options from 0 to 90 in increments of 10
  const roofAngleOptions = Array.from({ length: 10 }, (_, i) => i * 10);

  const apiKey = "AIzaSyCpAABUQR_MiKb4yDovC3-1jM0LdIs59Hc";

  return (
    <div className="roof-condition-step">
      <div className="step-header">
        <h3>2.1 Roof Area Measurement</h3>
        <p>Use our interactive map to precisely measure your roof area for accurate solar panel calculations.</p>
      </div>

      <div className="roof-measurement-container">
        <div className="map-instructions">
          <h4>How to Measure Your Roof Area</h4>
          <div className="instructions-grid">
            <div className="instruction-step">
              <span className="step-number">1</span>
              <p>Use the polygon tool to trace your roof outline</p>
            </div>
            <div className="instruction-step">
              <span className="step-number">2</span>
              <p>Click to add points around your roof perimeter</p>
            </div>
            <div className="instruction-step">
              <span className="step-number">3</span>
              <p>Double-click to complete the polygon</p>
            </div>
            <div className="instruction-step">
              <span className="step-number">4</span>
              <p>Drag points to adjust the shape for accuracy</p>
            </div>
          </div>
          <div className="map-tips">
            <h5>Tips for Accurate Measurement:</h5>
            <ul>
              <li>Trace only the usable roof area (avoid chimneys, skylights, dormers)</li>
              <li>Include multiple roof sections if needed</li>
              <li>The satellite view helps identify the exact roof edges</li>
              <li>Be as precise as possible - this affects your solar panel calculations</li>
            </ul>
          </div>
        </div>

        <div className="form-group">
          <label>Interactive Roof Area Measurement</label>
          <Wrapper apiKey={apiKey} render={render} libraries={['drawing', 'geometry']}>
            <GoogleMapsPolygonDrawer 
              onAreaChange={handleAreaChange}
              userLocation={userLocation}
              apiKey={apiKey}
            />
          </Wrapper>
        </div>

        {projectedRoofArea > 0 && (
          <div className="measurement-summary">
            <div className="summary-card">
              <h4>Measurement Complete</h4>
              <div className="measurement-details">
                <div className="detail-row">
                  <span>Horizontal Projected Area:</span>
                  <strong>{projectedRoofArea.toFixed(2)} m²</strong>
                </div>
                {roofConfig.roofAngle !== '' && (
                  <>
                    <div className="detail-row highlight">
                      <span>Actual Roof Area:</span>
                      <strong>{actualRoofArea.toFixed(2)} m²</strong>
                    </div>
                    <div className="detail-row">
                      <span>Area Adjustment Factor:</span>
                      <strong>×{(actualRoofArea / projectedRoofArea).toFixed(3)}</strong>
                    </div>
                  </>
                )}
                <div className="detail-row">
                  <span>Estimated Solar Panel Capacity:</span>
                  <strong>~{(actualRoofArea * 0.15).toFixed(1)} kW</strong>
                </div>
                <div className="detail-row">
                  <span>Approximate Panel Count:</span>
                  <strong>~{Math.floor(actualRoofArea / 2)} panels</strong>
                </div>
              </div>
              <small className="summary-note">
                *Estimates based on typical residential solar panel specifications (2m² per panel, 150W/m²)
                {roofConfig.roofAngle !== '' && (
                  <><br/>**Actual roof area calculated using: Roof Area = Projected Area ÷ cos({roofConfig.roofAngle}°)</>
                )}
              </small>
            </div>
          </div>
        )}

        {projectedRoofArea > 0 && (
          <div className="roof-configuration">
            <h4>Roof Configuration</h4>
            <p>Please provide additional details about your roof to optimize solar panel calculations.</p>

            <div className="config-grid">
              <div className="form-group">
                <label htmlFor="roofOrientation">Roof Orientation *</label>
                <select
                  id="roofOrientation"
                  value={roofConfig.roofOrientation}
                  onChange={(e) => handleConfigChange('roofOrientation', e.target.value)}
                  className="form-select"
                  required
                >
                  <option value="">Select orientation</option>
                  <option value="n">North (N)</option>
                  <option value="ne">Northeast (NE)</option>
                  <option value="ene">East-Northeast (ENE)</option>
                  <option value="e">East (E)</option>
                  <option value="ese">East-Southeast (ESE)</option>
                  <option value="se">Southeast (SE)</option>
                  <option value="s">South (S)</option>
                  <option value="sw">Southwest (SW)</option>
                  <option value="wsw">West-Southwest (WSW)</option>
                  <option value="w">West (W)</option>
                  <option value="wnw">West-Northwest (WNW)</option>
                  <option value="nw">Northwest (NW)</option>
                </select>
                <small>Direction your roof faces - affects solar energy generation</small>
              </div>

              <div className="form-group">
                <label htmlFor="roofAngle">Roof Angle *</label>
                <select
                  id="roofAngle"
                  value={roofConfig.roofAngle}
                  onChange={(e) => handleConfigChange('roofAngle', e.target.value)}
                  className="form-select"
                  required
                >
                  <option value="">Select angle</option>
                  {roofAngleOptions.map(angle => (
                    <option key={angle} value={angle}>{angle}° {angle === 0 ? '(Flat)' : ''}</option>
                  ))}
                </select>
                <small>Roof slope angle in degrees - automatically adjusts roof area calculation</small>
              </div>

              <div className="form-group">
                <label htmlFor="roofShading">Roof Shading *</label>
                <select
                  id="roofShading"
                  value={roofConfig.roofShading}
                  onChange={(e) => handleConfigChange('roofShading', e.target.value)}
                  className="form-select"
                  required
                >
                  <option value="">Select shading level</option>
                  <option value="no-shading">No Shading</option>
                  <option value="light">Light Shading</option>
                  <option value="moderate">Moderate Shading</option>
                  <option value="heavy">Heavy Shading</option>
                  <option value="extreme">Extreme Shading</option>
                </select>
                <small>Amount of shade from trees, buildings, or other obstructions</small>
              </div>

              <div className="form-group">
                <label htmlFor="roofUtilisation">Roof Utilisation *</label>
                <select
                  id="roofUtilisation"
                  value={roofConfig.roofUtilisation}
                  onChange={(e) => handleConfigChange('roofUtilisation', e.target.value)}
                  className="form-select"
                  required
                >
                  <option value="">Select complexity level</option>
                  <option value="minimal-obstacles">Minimal Obstacles</option>
                  <option value="slightly-complex">Slightly Complex</option>
                  <option value="moderately-complex">Moderately Complex</option>
                  <option value="highly-complex">Highly Complex</option>
                  <option value="extremely-complex">Extremely Complex</option>
                </select>
                <small>Roof complexity considering chimneys, vents, and other obstacles</small>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RoofCondition;
