import React, { useEffect, useState } from "react";
import EnergyDemandChart from "../EnergyDemandChart";
import "../../styles/energy-demand.css";
import "../../styles/results.css";

const Results = ({ data, calculationResult, setCalculationResult, onBack }) => {
  const [loading, setLoading] = useState(true);

  // Function to get solar potential rating based on percentage
  const getSolarPotentialRating = (percentage) => {
    if (percentage > 95) {
      return { rating: 'Excellent', className: 'excellent', recommended: true };
    } else if (percentage >= 90) {
      return { rating: 'Very Good', className: 'very-good', recommended: true };
    } else if (percentage >= 80) {
      return { rating: 'Good', className: 'good', recommended: true };
    } else if (percentage >= 60) {
      return { rating: 'Medium', className: 'medium', recommended: true };
    } else {
      return { rating: 'Poor', className: 'poor', recommended: false };
    }
  };

  const getOrientationLabel = (orientation) => {
    const orientationLabels = {
      'N': 'North',
      'NE': 'Northeast', 
      'E': 'East',
      'SE': 'Southeast',
      'S': 'South',
      'SW': 'Southwest',
      'W': 'West',
      'NW': 'Northwest'
    };
    return orientationLabels[orientation] || orientation;
  };

  const getShadingLabel = (shading) => {
    const shadingLabels = {
      'no-shading': 'No Shading',
      'light-shading': 'Light Shading',
      'moderate-shading': 'Moderate Shading', 
      'heavy-shading': 'Heavy Shading'
    };
    return shadingLabels[shading] || shading;
  };

  const getUtilisationLabel = (utilisation) => {
    const utilisationLabels = {
      'minimal-obstacles': 'Minimal Obstacles',
      'some-obstacles': 'Some Obstacles',
      'many-obstacles': 'Many Obstacles'
    };
    return utilisationLabels[utilisation] || utilisation;
  };

  // Function to convert frontend heating method to backend heating type
  const convertHeatingMethod = (heatingMethod) => {
    switch (heatingMethod) {
      case "gas boiler":
        return "gas";
      case "electricity heating":
        return "electric";
      case "heat pumps":
        return "heat-pump";
      default:
        return "gas"; // default fallback
    }
  };

  // Function to convert frontend hot water method to backend hot water type
  const convertHotWaterMethod = (hotWaterMethod) => {
    switch (hotWaterMethod) {
      case "gas boiler":
        return "gas-boiler";
      case "electricity":
        return "electricity";
      case "heat pumps":
        return "heat-pump";
      default:
        return "gas-boiler"; // default fallback
    }
  };

  useEffect(() => {
    if (data) {
      // We have data, so we can display the results
      // Energy Demand component will handle its own loading
      setLoading(false);
    }
  }, [data]);

  if (loading) {
    return (
      <div className="results-container">
        <h2>Loading results...</h2>
        <p>Please wait while we prepare your renewable energy system analysis.</p>
      </div>
    );
  }

  if (!data) {
    return (
      <div className="results-container">
        <h2>No Data Available</h2>
        <p>Please complete the previous steps to see your results.</p>
        <div className="button-group">
          <button type="button" className="btn btn-secondary" onClick={onBack}>
            Back to Tariffs
          </button>
        </div>
      </div>
    );
  }

  // Extract selected roofs data
  const selectedRoofs = data.selectedRoofs || data.addedRoofs?.filter(roof => roof.selected) || [];

  // Calculate totals
  const totalSelectedArea = selectedRoofs.reduce((sum, roof) => sum + (roof.area || 0), 0);
  const totalSolarPanels = selectedRoofs.reduce((sum, roof) => sum + (roof.solarPanelCount || 0), 0);
  const averageSolarPotential = selectedRoofs.length > 0 
    ? selectedRoofs.reduce((sum, roof) => sum + (roof.solarPotential || 0), 0) / selectedRoofs.length
    : 0;

  // Prepare user data for Energy Demand component
  const userData = {
    occupants: data.householdSize || data.occupants || 3,
    hotWaterType: convertHotWaterMethod(data.hotWaterMethod),
    heatingType: convertHeatingMethod(data.heatingMethod),
    houseArea: data.floorArea || data.houseArea || 100,
    buildYear: data.buildingYear || data.buildYear || '1981-2002',
    wallType: data.wallType || 'modern',  // Updated default to match new options
    windowType: data.windowType || 'double',
    houseType: data.propertyType || data.houseType || 'semi-detached',
    epcRating: data.epcRating || 'D',
    roofInsulation: data.roofInsulation || 'no',
    floorInsulation: data.floorInsulation || 'no'
  };

  console.log('Results data:', data); // Debug log
  console.log('Selected roofs:', selectedRoofs); // Debug log
  console.log('User data for energy demand:', userData); // Debug log

  return (
    <div className="results-container">
      <h2>Renewable Energy System Results</h2>

      {/* Solar Potential Summary */}
      {selectedRoofs.length > 0 && (
        <div className="results-section solar-potential-section">
          <div className="solar-potential-card">
            <h3>Solar Potential Summary</h3>

            {/* Total Summary */}
            <div className="total-summary">
              <div className="total-summary-grid">
                <div className="total-stat-item">
                  <span className="stat-label">Selected Roofs</span>
                  <span className="stat-value">{selectedRoofs.length} roof{selectedRoofs.length > 1 ? 's' : ''}</span>
                </div>
                <div className="total-stat-item">
                  <span className="stat-label">Total Area</span>
                  <span className="stat-value">{totalSelectedArea.toFixed(0)} m²</span>
                </div>
                <div className="total-stat-item">
                  <span className="stat-label">Total Estimated Panels</span>
                  <span className="stat-value">Fits for {totalSolarPanels} panels</span>
                </div>
                <div className="total-stat-item">
                  <span className="stat-label">Average Solar Potential</span>
                  <span className="stat-value">
                    {averageSolarPotential.toFixed(1)}%
                    <span className={`rating-badge ${getSolarPotentialRating(averageSolarPotential).className}`}>
                      {getSolarPotentialRating(averageSolarPotential).rating}
                    </span>
                  </span>
                </div>
              </div>

              {!getSolarPotentialRating(averageSolarPotential).recommended && (
                <div className="warning-message">
                  The average solar potential is rated as "Poor". Solar installation may not be cost-effective for your property.
                </div>
              )}
            </div>

            {/* Individual Roof Details */}
            <div className="individual-roofs">
              <h4>Individual Roof Details</h4>
              <div className="roof-list">
                {selectedRoofs.map((roof, index) => {
                  const rating = getSolarPotentialRating(roof.solarPotential);
                  return (
                    <div key={roof.id} className="roof-summary-item">
                      <div className="roof-summary-header">
                        <h5>Roof {roof.id}</h5>
                        <span className={`rating-badge-small ${rating.className}`}>
                          {rating.rating}
                        </span>
                      </div>
                      <div className="roof-summary-details">
                        <div className="roof-summary-grid">
                          <div className="summary-detail">
                            <span>Area:</span>
                            <span>{roof.area.toFixed(0)} m²</span>
                          </div>
                          <div className="summary-detail">
                            <span>Orientation:</span>
                            <span>{getOrientationLabel(roof.orientation)}</span>
                          </div>
                          <div className="summary-detail">
                            <span>Angle:</span>
                            <span>{roof.angle}°</span>
                          </div>
                          <div className="summary-detail">
                            <span>Shading:</span>
                            <span>{getShadingLabel(roof.shading)}</span>
                          </div>
                          <div className="summary-detail">
                            <span>Obstacles:</span>
                            <span>{getUtilisationLabel(roof.utilisation)}</span>
                          </div>
                          <div className="summary-detail">
                            <span>Estimated Fits For:</span>
                            <span>{roof.solarPanelCount || 0} panels</span>
                          </div>
                          <div className="summary-detail solar-potential-highlight">
                            <span>Solar Potential:</span>
                            <span className="solar-potential-value">
                              {roof.solarPotential.toFixed(1)}%
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      )}

      {selectedRoofs.length === 0 && (
        <div className="results-section">
          <div className="no-roofs-message">
            <h3>No Solar Installation Selected</h3>
            <p>You haven't selected any roofs for solar panel installation. Please go back to the roof configuration step to add and select roofs.</p>
          </div>
        </div>
      )}

      {/* Energy Demand Section */}
      <div className="results-section energy-demand-section">
        <EnergyDemandChart userData={userData} />
      </div>

      {/* Placeholder for other results sections */}
      <div className="results-placeholder">
        <p>Additional results sections will be implemented here...</p>
      </div>

      <div className="button-group">
        <button type="button" className="btn btn-secondary" onClick={onBack}>
          Back to Tariffs
        </button>
      </div>
    </div>
  );
};

export default Results;
